    46→    [Subscribe]
    47→    private void OnLoadLevelRequest(in LoadLevelRequest request)
    48→    {
    49→        if (!IsEnabled) return;
    50→
    51→        // Only handle Blender levels
    52→        if (!request.LevelIdentifier.StartsWith("Blender_"))
    53→            return;
    54→
    55→        Console.WriteLine($"Loading Blender level: {request.LevelIdentifier}");
    56→
    57→        // Clean up previous entities
    58→        CleanupEntities();
    59→
    60→        try
    61→        {
    62→            // Load JSON file from Content directory
    63→            // The Content.RootDirectory gives us the path to the Content folder
    64→            var jsonPath = Path.Combine(AppDomain.CurrentDomain.BaseDirectory, _content.RootDirectory, "blender_level.json");
    65→            Console.WriteLine($"Loading Blender level from: {jsonPath}");
    66→            var jsonContent = File.ReadAllText(jsonPath);
    67→
    68→            var options = new JsonSerializerOptions
    69→            {
    70→                PropertyNameCaseInsensitive = true
    71→            };
    72→
    73→            var levelData = JsonSerializer.Deserialize<BlenderLevelData>(jsonContent, options);
    74→
    75→            if (levelData?.Objects == null)
    76→            {
    77→                Console.WriteLine("No objects found in Blender level data.");
    78→                return;
    79→            }
    80→
    81→            Console.WriteLine($"Loaded {levelData.Objects.Count} objects from Blender level.");
    82→
    83→            foreach (var obj in levelData.Objects)
    84→            {
    85→                switch (obj.Type)
    86→                {
    87→                    case "CAMERA":
    88→                        ProcessCamera(obj, levelData.ScaleFactor);
    89→                        break;
    90→                    case "MESH":
    91→                    case "GREASEPENCIL":
    92→                        // GREASEPENCIL objects are rendered to PNG and exported as planes
    93→                        // They are processed the same way as MESH objects
    94→                        ProcessMesh(obj);
    95→                        break;
    96→                    case "EMPTY":
    97→                        // Could be used for spawn points, triggers, etc.
    98→                        Console.WriteLine($"Empty object: {obj.Name} at ({obj.Position?.X}, {obj.Position?.Y})");
    99→                        break;
   100→                    default:
   101→                        Console.WriteLine($"Skipping object type: {obj.Type}");
   102→                        break;
   103→                }
   104→            }
   105→
   106→            Console.WriteLine($"Created {_blenderEntities.Count} entities from Blender level.");
   107→        }
   108→        catch (Exception ex)
   109→        {
   110→            Console.WriteLine($"Error loading Blender level: {ex.Message}\n{ex.StackTrace}");
   111→        }
   112→    }
