   114→    private void ProcessCamera(BlenderObject cameraObj, float scaleFactor)
   115→    {
   116→        if (cameraObj.Position == null) return;
   117→
   118→        var cameraPosition = new Vector2(cameraObj.Position.X, cameraObj.Position.Y);
   119→        _camera.Position = cameraPosition;
   120→
   121→        // Read ortho_scale from custom properties and convert to game zoom
   122→        // Blender ortho_scale = visible width in Blender units
   123→        // With scaleFactor, visible world pixels = ortho_scale * scaleFactor
   124→        // Game zoom = virtualWidth / visible_world_pixels
   125→        if (cameraObj.CustomProperties?.TryGetValue("zoom", out var zoomValue) == true)
   126→        {
   127→            if (zoomValue is JsonElement jsonElement && jsonElement.TryGetDouble(out var orthoScale))
   128→            {
   129→                // Convert Blender ortho_scale to game zoom
   130→                var visibleWorldWidth = orthoScale * scaleFactor;
   131→                var gameZoom = _camera.VirtualWidth / visibleWorldWidth;
   132→                _camera.Zoom = (float)gameZoom;
   133→                Console.WriteLine($"Set camera zoom to {gameZoom} (from Blender ortho_scale {orthoScale}, scaleFactor {scaleFactor})");
   134→            }
   135→        }
   136→
   137→        Console.WriteLine($"Set camera position to ({cameraPosition.X}, {cameraPosition.Y})");
   138→    }
   139→
   140→    private void ProcessMesh(BlenderObject meshObj)
   141→    {
   142→        if (meshObj.UvMapping == null)
   143→        {
   144→            Console.WriteLine($"Skipping mesh '{meshObj.Name}' - no UV mapping.");
   145→            return;
   146→        }
   147→
   148→        // Extract content path from texture path
   149→        var contentPath = ExtractContentPath(meshObj.UvMapping.TexturePath);
   150→        if (string.IsNullOrEmpty(contentPath))
   151→        {
   152→            Console.WriteLine($"Skipping mesh '{meshObj.Name}' - invalid texture path: {meshObj.UvMapping.TexturePath}");
   153→            return;
   154→        }
   155→
   156→        // Load texture
   157→        Texture2D texture;
   158→        if (!_loadedTextures.TryGetValue(contentPath, out texture))
   159→        {
   160→            try
   161→            {
   162→                texture = _content.Load<Texture2D>(contentPath);
   163→                _loadedTextures[contentPath] = texture;
   164→                Console.WriteLine($"Loaded texture: {contentPath}");
   165→            }
   166→            catch (Exception ex)
   167→            {
   168→                Console.WriteLine($"Failed to load texture '{contentPath}': {ex.Message}");
   169→                return;
   170→            }
   171→        }
   172→
   173→        // Get UV coordinates from first layer
   174→        var uvLayer = meshObj.UvMapping.UvLayers?.FirstOrDefault();
   175→        if (uvLayer?.UvCoordinates == null || uvLayer.UvCoordinates.Count == 0)
   176→        {
   177→            Console.WriteLine($"Skipping mesh '{meshObj.Name}' - no UV coordinates.");
   178→            return;
   179→        }
   180→
   181→        // Calculate source rectangle from UV coordinates
   182→        var sourceRect = CalculateSourceRect(uvLayer.UvCoordinates, texture);
   183→
   184→        // Create entity
   185→        var entity = _world.CreateEntity();
   186→        _blenderEntities.Add(entity);
   187→
   188→        // Position from Blender
   189→        var position = new Vector2(
   190→            meshObj.Position?.X ?? 0,
   191→            meshObj.Position?.Y ?? 0
   192→        );
   193→
   194→        // Transform
   195→        entity.Set(new Transform(position));
   196→
   197→        // SpriteInfo with calculated source rectangle
   198→        // Use mesh dimensions from Blender for Size (already scaled), fallback to source rect if not available
   199→        var size = new Vector2(
   200→            meshObj.Dimensions?.X ?? sourceRect.Width,
   201→            meshObj.Dimensions?.Y ?? sourceRect.Height
   202→        );
   203→
   204→        // Calculate origin from Blender's origin offset
   205→        // Blender origin offset is normalized (0-1) where (0,0) is bottom-left and (0.5,0.5) is center
   206→        // SpriteBatch.Draw expects origin in SOURCE TEXTURE coordinates (not destination size)
   207→        var originOffsetX = meshObj.OriginOffset?.X ?? 0.5f;  // Default to center
   208→        var originOffsetY = meshObj.OriginOffset?.Y ?? 0.5f;
   209→        // Y is inverted: Blender Y=0 is bottom, engine Y=0 is top
   210→        // Use sourceRect dimensions since SpriteBatch.Draw origin is in source coordinates
   211→        var origin = new Vector2(sourceRect.Width * originOffsetX, sourceRect.Height * (1 - originOffsetY));
   212→
   213→        entity.Set(new SpriteInfo
   214→        {
   215→            SpriteSheet = texture,
   216→            Source = sourceRect,
   217→            Size = size,
   218→            Color = Color.White,
   219→            Target = RenderTargetID.Main,
   220→            LayerDepth = DEFAULT_LAYER_DEPTH,
   221→            Origin = origin
   222→        });
   223→
   224→        // DrawComponent
   225→        entity.Set(new DrawComponent
   226→        {
   227→            Type = DrawElementType.Sprite,
   228→            Target = RenderTargetID.Main
   229→        });
   230→
   231→        // Make visible
   232→        entity.Set(new Visible());
   233→
   234→        // Process collection-based components
   235→        ProcessCollections(entity, meshObj, size);
   236→
   237→        Console.WriteLine($"Created entity for mesh '{meshObj.Name}' at ({position.X}, {position.Y}) with size ({size.X}, {size.Y}), origin ({origin.X}, {origin.Y}), and source rect ({sourceRect.X}, {sourceRect.Y}, {sourceRect.Width}, {sourceRect.Height})");
   238→    }
   239→
   240→    /// <summary>
   241→    /// Extracts the MonoGame Content path from a full file system path.
   242→    /// Finds "/Content/" and returns the substring after it, without file extension.
   243→    /// </summary>
   244→    private static string ExtractContentPath(string fullPath)
   245→    {
   246→        if (string.IsNullOrEmpty(fullPath))
   247→            return null;
   248→
   249→        var contentIndex = fullPath.IndexOf("/Content/", StringComparison.OrdinalIgnoreCase);
   250→        if (contentIndex < 0)
   251→        {
   252→            // Try Windows-style path
   253→            contentIndex = fullPath.IndexOf("\\Content\\", StringComparison.OrdinalIgnoreCase);
   254→        }
   255→
   256→        if (contentIndex < 0)
   257→            return null;
   258→
   259→        var separatorLength = "/Content/".Length;
   260→        var relativePath = fullPath.Substring(contentIndex + separatorLength);
   261→
   262→        // Remove file extension
   263→        var extensionIndex = relativePath.LastIndexOf('.');
   264→        if (extensionIndex > 0)
   265→        {
   266→            relativePath = relativePath.Substring(0, extensionIndex);
   267→        }
   268→
   269→        return relativePath;
   270→    }
   271→
   272→    /// <summary>
   273→    /// Calculates a source rectangle from UV coordinates.
   274→    /// Assumes a plane mesh with 4 vertices.
   275→    /// </summary>
   276→    private static Rectangle CalculateSourceRect(List<UvCoordinate> uvCoords, Texture2D texture)
   277→    {
   278→        if (uvCoords == null || uvCoords.Count == 0)
   279→            return Rectangle.Empty;
   280→
   281→        float minU = uvCoords.Min(uv => uv.U);
   282→        float maxU = uvCoords.Max(uv => uv.U);
   283→        float minV = uvCoords.Min(uv => uv.V);
   284→        float maxV = uvCoords.Max(uv => uv.V);
   285→
   286→        // Convert UV to pixel coordinates with rounding for better precision
   287→        // UV origin is bottom-left, texture origin is top-left, so flip V
   288→        int x = (int)Math.Round(minU * texture.Width);
   289→        int y = (int)Math.Round((1 - maxV) * texture.Height);
   290→        int width = (int)Math.Round((maxU - minU) * texture.Width);
   291→        int height = (int)Math.Round((maxV - minV) * texture.Height);
   292→
   293→        return new Rectangle(x, y, width, height);
   294→    }
   295→
   296→    /// <summary>
   297→    /// Processes Blender collection membership and adds appropriate components.
   298→    /// Collection names determine entity behavior (e.g., "Collision" adds BoxCollider).
   299→    /// Collection custom properties can configure component settings.
   300→    /// </summary>
   301→    private void ProcessCollections(Entity entity, BlenderObject meshObj, Vector2 size)
   302→    {
   303→        if (meshObj.Collections == null || meshObj.Collections.Count == 0)
   304→            return;
   305→
   306→        // Compute visual offset from origin so collision boxes align with rendered sprites.
   307→        // SpriteBatch.Draw renders at: position - origin * scale
   308→        // In destination space: offset = -(size * originOffset)
   309→        var originOffsetX = meshObj.OriginOffset?.X ?? 0.5f;
   310→        var originOffsetY = meshObj.OriginOffset?.Y ?? 0.5f;
   311→        // Compute float edges of visual bounds relative to entity position
   312→        float left = -size.X * originOffsetX;
   313→        float top = -size.Y * (1 - originOffsetY);  // Y inverted: Blender Y=0 is bottom
   314→        // Snap to smallest enclosing integer rectangle (floor expands left/top, ceiling expands right/bottom)
   315→        int intLeft = (int)Math.Floor(left);
   316→        int intTop = (int)Math.Floor(top);
   317→        int intRight = (int)Math.Ceiling(left + size.X);
   318→        int intBottom = (int)Math.Ceiling(top + size.Y);
   319→
   320→        var boundsOffset = new Point(intLeft, intTop);
   321→        var boundsSize = new Point(intRight - intLeft, intBottom - intTop);
   322→
   323→        // Check for Collision collection
   324→        if (meshObj.Collections.Contains("Collision"))
   325→        {
   326→            entity.Set(new EntityInfo("Collision"));
   327→                
   328→            // Get collision layer from collection properties (default to -1 for all layers)
   329→            int layer = -1;
   330→            bool passive = false;
   331→
   332→            if (meshObj.CollectionProperties?.TryGetValue("Collision", out var collisionProps) == true)
   333→            {
   334→                layer = GetIntProperty(collisionProps, "layer", -1);
   335→                passive = GetBoolProperty(collisionProps, "passive", false);
   336→            }
   337→
   338→            var bounds = new Rectangle(boundsOffset, boundsSize);
   339→            var activeLayers = new HashSet<int> { layer };
   340→            entity.Set(new BoxCollider(bounds, activeLayers, passive));
   341→
   342→            Console.WriteLine($"Added BoxCollider to '{meshObj.Name}' (layer: {layer}, passive: {passive})");
   343→        }
   344→
   345→        // Check for Player collection
   346→        if (meshObj.Collections.Contains("Player"))
   347→        {
   348→            entity.Set(new EntityInfo("Player"));
   349→            entity.Set(new BoxCollider(new Rectangle(boundsOffset, boundsSize)));
   350→            entity.Set(new RigidBody());
   351→            entity.Set(new Velocity());
   352→
   353→            // Add camera follow target component
   354→            entity.Set(new CameraFollowTarget
   355→            {
   356→                DampingX = 5.0f,
   357→                DampingY = 5.0f,
   358→                MaxDistanceX = 150.0f,
   359→                MaxDistanceY = 100.0f,
   360→                IsActive = true
   361→            });
   362→            Console.WriteLine($"Set Player for '{meshObj.Name}'");
   363→        }
   364→
   365→        // Check for Enemy collection
   366→        if (meshObj.Collections.Contains("Enemy"))
   367→        {
   368→            entity.Set(new EntityInfo("Enemy"));
   369→            Console.WriteLine($"Set Enemy for '{meshObj.Name}'");
   370→        }
   371→
   372→        // Check for Trigger collection (collision but no physics push)
   373→        if (meshObj.Collections.Contains("Trigger"))
   374→        {
   375→            if (!entity.Has<BoxCollider>())
   376→            {
   377→                var bounds = new Rectangle(boundsOffset, boundsSize);
   378→                entity.Set(new BoxCollider(bounds, passive: true));
   379→            }
   380→            entity.Set(new EntityInfo("Trigger"));
   381→            Console.WriteLine($"Set as Trigger zone for '{meshObj.Name}'");
   382→        }
   383→    }
   384→
   385→    /// <summary>
   386→    /// Gets an integer property from collection properties dictionary.
   387→    /// </summary>
   388→    private static int GetIntProperty(Dictionary<string, object> props, string key, int defaultValue)
   389→    {
   390→        if (props == null || !props.TryGetValue(key, out var value))
   391→            return defaultValue;
   392→
   393→        if (value is JsonElement jsonElement)
   394→        {
   395→            if (jsonElement.TryGetInt32(out var intVal))
   396→                return intVal;
   397→            if (jsonElement.TryGetDouble(out var doubleVal))
   398→                return (int)doubleVal;
   399→        }
   400→
   401→        if (value is int intValue)
   402→            return intValue;
   403→        if (value is double doubleValue)
   404→            return (int)doubleValue;
   405→        if (value is float floatValue)
   406→            return (int)floatValue;
   407→
   408→        return defaultValue;
   409→    }
   410→
   411→    /// <summary>
   412→    /// Gets a boolean property from collection properties dictionary.
   413→    /// </summary>
   414→    private static bool GetBoolProperty(Dictionary<string, object> props, string key, bool defaultValue)
   415→    {
   416→        if (props == null || !props.TryGetValue(key, out var value))
   417→            return defaultValue;
   418→
   419→        if (value is JsonElement jsonElement)
   420→        {
   421→            if (jsonElement.ValueKind == JsonValueKind.True)
   422→                return true;
   423→            if (jsonElement.ValueKind == JsonValueKind.False)
   424→                return false;
   425→        }
   426→
   427→        if (value is bool boolValue)
   428→            return boolValue;
   429→
   430→        return defaultValue;
   431→    }
   432→
   433→    private void CleanupEntities()
   434→    {
   435→        Console.WriteLine($"Cleaning up {_blenderEntities.Count} Blender entities...");
   436→        foreach (var entity in _blenderEntities.ToArray())
   437→        {
   438→            if (entity.IsAlive)
   439→            {
   440→                entity.Dispose();
   441→            }
   442→        }
   443→        _blenderEntities.Clear();
   444→    }
   445→
   446→    public void Update(GameState state)
   447→    {
   448→        // Processing happens via message subscription
   449→    }
   450→
   451→    public void Dispose()
   452→    {
   453→        CleanupEntities();
   454→        _loadedTextures.Clear();
   455→        GC.SuppressFinalize(this);
   456→    }
   457→}
