  1→using System.Text.Json;
  2→using DefaultEcs;
  3→using DefaultEcs.System;
  4→using Microsoft.Xna.Framework;
  5→using Microsoft.Xna.Framework.Content;
  6→using Microsoft.Xna.Framework.Graphics;
  7→using System;
  8→using System.Collections.Generic;
  9→using System.IO;
 10→using System.Linq;
 11→using MonoDreams.Component;
 12→using MonoDreams.Component.Collision;
 13→using MonoDreams.Component.Physics;
 14→using MonoDreams.Component.Draw;
 15→using MonoDreams.Level;
 16→using MonoDreams.Message.Level;
 17→using MonoDreams.Draw;
 18→using MonoDreams.Extension;
 19→using MonoDreams.State;
 20→using Logger = MonoDreams.State.Logger;
 21→
 22→namespace MonoDreams.System.Level;
 23→
 24→/// <summary>
 25→/// Parses Blender-exported JSON level files and creates entities for meshes.
 26→/// Handles LoadLevelRequest messages where the identifier starts with "Blender_".
 27→/// </summary>
 28→public sealed class BlenderLevelParserSystem : ISystem<GameState>
 29→{
 30→    private const float DEFAULT_LAYER_DEPTH = 0.5f;
 31→
 32→    private readonly World _world;
 33→    private readonly ContentManager _content;
 34→    private readonly MonoDreams.Component.Camera _camera;
 35→    private readonly Dictionary<string, Texture2D> _loadedTextures = new();
 36→    private readonly HashSet<Entity> _blenderEntities = new();
 37→    private readonly Dictionary<string, Entity> _nameToEntity = new();
 38→    private readonly Dictionary<string, Action<Entity, BlenderObject>> _collectionHandlers = new();
 39→    private DrawLayerMap _drawLayerMap;
 40→
 41→    public bool IsEnabled { get; set; } = true;
 42→
 43→    public BlenderLevelParserSystem(World world, ContentManager content, MonoDreams.Component.Camera camera)
 44→    {
 45→        _world = world ?? throw new ArgumentNullException(nameof(world));
 46→        _content = content ?? throw new ArgumentNullException(nameof(content));
 47→        _camera = camera ?? throw new ArgumentNullException(nameof(camera));
 48→
 49→        _world.Subscribe<LoadLevelRequest>(OnLoadLevelRequest);
 50→    }
 51→
 52→    /// <summary>
 53→    /// Registers a handler that will be called for entities belonging to the specified Blender collection.
 54→    /// This allows game code to add game-specific components to entities created by the core parser.
 55→    /// </summary>
 56→    public void RegisterCollectionHandler(string collectionName, Action<Entity, BlenderObject> handler)
 57→    {
 58→        _collectionHandlers[collectionName] = handler ?? throw new ArgumentNullException(nameof(handler));
 59→    }
 60→
 61→    /// <summary>
 62→    /// Sets the draw layer map used for depth resolution. When set, mesh depth is resolved by:
 63→    /// 1. Object's "drawLayer" custom property (string matching a layer name)
 64→    /// 2. Blender collection names matched against layer names
 65→    /// 3. Falls back to DEFAULT_LAYER_DEPTH
 66→    /// </summary>
 67→    public void SetDrawLayerMap(DrawLayerMap drawLayerMap)
 68→    {
 69→        _drawLayerMap = drawLayerMap ?? throw new ArgumentNullException(nameof(drawLayerMap));
 70→    }
 71→
 72→    [Subscribe]
 73→    private void OnLoadLevelRequest(in LoadLevelRequest request)
 74→    {
 75→        if (!IsEnabled) return;
 76→
 77→        // Only handle Blender levels
 78→        if (!request.LevelIdentifier.StartsWith("Blender_"))
 79→            return;
 80→
 81→        Logger.Info($"Loading Blender level: {request.LevelIdentifier}");
 82→
 83→        // Clean up previous entities
 84→        CleanupEntities();
 85→
 86→        try
 87→        {
 88→            // Load JSON file from Content directory
 89→            // The Content.RootDirectory gives us the path to the Content folder
 90→            var jsonPath = Path.Combine(AppDomain.CurrentDomain.BaseDirectory, _content.RootDirectory, "blender_level.json");
 91→            Logger.Debug($"Loading Blender level from: {jsonPath}");
 92→            var jsonContent = File.ReadAllText(jsonPath);
 93→
 94→            var options = new JsonSerializerOptions
 95→            {
 96→                PropertyNameCaseInsensitive = true
 97→            };
 98→
 99→            var levelData = JsonSerializer.Deserialize<BlenderLevelData>(jsonContent, options);
100→
101→            if (levelData?.Objects == null)
102→            {
103→                Logger.Warning("No objects found in Blender level data.");
104→                return;
105→            }
106→
107→            Logger.Info($"Loaded {levelData.Objects.Count} objects from Blender level.");
108→
109→            // Pre-scan: identify collider children (names ending with "-collider" that have a parent)
110→            var colliderChildMap = new Dictionary<string, BlenderObject>();   // parent name → collider data
111→            var colliderChildNames = new HashSet<string>();
112→            foreach (var obj in levelData.Objects)
113→            {
114→                if (obj.Name.EndsWith("-collider") && !string.IsNullOrEmpty(obj.Parent))
115→                {
116→                    colliderChildMap[obj.Parent] = obj;
117→                    colliderChildNames.Add(obj.Name);
118→                    Logger.Debug($"Found collider child '{obj.Name}' for parent '{obj.Parent}'.");
119→                }
120→            }
121→
122→            // Pass 1: Create all entities (skip collider children)
123→            foreach (var obj in levelData.Objects)
124→            {
125→                if (colliderChildNames.Contains(obj.Name))
126→                {
127→                    Logger.Debug($"Skipping collider child '{obj.Name}' (will be applied to parent).");
128→                    continue;
129→                }
130→
131→                switch (obj.Type)
132→                {
133→                    case "CAMERA":
134→                        ProcessCamera(obj, levelData.ScaleFactor);
135→                        break;
136→                    case "MESH":
137→                    case "GREASEPENCIL":
138→                        ProcessMesh(obj, levelData);
139→                        break;
140→                    case "EMPTY":
141→                        ProcessEmpty(obj);
142→                        break;
143→                    default:
144→                        Logger.Warning($"Skipping unknown object type: {obj.Type}");
145→                        break;
146→                }
147→            }
148→
149→            // Post-Pass 1: Apply collider children to parent entities
150→            foreach (var (parentName, colliderObj) in colliderChildMap)
151→            {
152→                ApplyColliderChild(parentName, colliderObj);
153→            }
154→
155→            // Pass 2: Set up parent-child relationships
156→            foreach (var obj in levelData.Objects)
157→            {
158→                if (string.IsNullOrEmpty(obj.Parent)) continue;
159→                if (colliderChildNames.Contains(obj.Name)) continue;
160→
161→                if (!_nameToEntity.TryGetValue(obj.Name, out var childEntity))
162→                {
163→                    Logger.Warning($"Parent linkage: child '{obj.Name}' not found in entity map.");
164→                    continue;
165→                }
166→                if (!_nameToEntity.TryGetValue(obj.Parent, out var parentEntity))
167→                {
168→                    Logger.Warning($"Parent linkage: parent '{obj.Parent}' not found for child '{obj.Name}'.");
169→                    continue;
170→                }
171→
172→                childEntity.SetParent(parentEntity);
173→                Logger.Debug($"Set parent of '{obj.Name}' to '{obj.Parent}'.");
174→            }
175→
176→            Logger.Info($"Created {_blenderEntities.Count} entities from Blender level.");
177→        }
178→        catch (Exception ex)
179→        {
180→            Logger.Error($"Error loading Blender level: {ex.Message}\n{ex.StackTrace}");
181→        }
182→    }
183→
184→    private void ProcessCamera(BlenderObject cameraObj, float scaleFactor)
185→    {
186→        if (cameraObj.Position == null) return;
187→
188→        var cameraPosition = new Vector2(cameraObj.Position.X, cameraObj.Position.Y);
189→        _camera.Position = cameraPosition;
190→
191→        // Read ortho_scale from custom properties and convert to game zoom
192→        // Blender ortho_scale = visible width in Blender units
193→        // With scaleFactor, visible world pixels = ortho_scale * scaleFactor
194→        // Game zoom = virtualWidth / visible_world_pixels
195→        if (cameraObj.CustomProperties?.TryGetValue("zoom", out var zoomValue) == true)
196→        {
197→            if (zoomValue is JsonElement jsonElement && jsonElement.TryGetDouble(out var orthoScale))
198→            {
199→                // Convert Blender ortho_scale to game zoom
200→                var visibleWorldWidth = orthoScale * scaleFactor;
201→                var gameZoom = _camera.VirtualWidth / visibleWorldWidth;
202→                _camera.Zoom = (float)gameZoom;
203→                Logger.Debug($"Set camera zoom to {gameZoom} (from Blender ortho_scale {orthoScale}, scaleFactor {scaleFactor})");
204→            }
205→        }
206→
207→        Logger.Debug($"Set camera position to ({cameraPosition.X}, {cameraPosition.Y})");
208→    }
209→
210→    private void ProcessEmpty(BlenderObject emptyObj)
211→    {
212→        var entity = _world.CreateEntity();
213→        _blenderEntities.Add(entity);
214→
215→        var position = new Vector2(
216→            emptyObj.Position?.X ?? 0,
217→            emptyObj.Position?.Y ?? 0
218→        );
219→
220→        entity.Set(new Transform(position));
221→        entity.Set(new EntityInfo("Empty", emptyObj.Name));
222→
223→        _nameToEntity[emptyObj.Name] = entity;
224→
225→        // Invoke registered game-specific collection handlers
226→        if (emptyObj.Collections != null)
227→        {
228→            foreach (var collection in emptyObj.Collections)
229→            {
230→                if (_collectionHandlers.TryGetValue(collection, out var handler))
231→                {
232→                    handler(entity, emptyObj);
233→                }
234→            }
235→        }
236→
237→        Logger.Debug($"Created empty entity '{emptyObj.Name}' at ({position.X}, {position.Y})");
238→    }
239→
240→    private void ProcessMesh(BlenderObject meshObj, BlenderLevelData levelData)
241→    {
242→        if (meshObj.UvMapping == null)
243→        {
244→            Logger.Warning($"Skipping mesh '{meshObj.Name}' - no UV mapping.");
245→            return;
246→        }
247→
248→        // Extract content path from texture path
249→        var contentPath = ExtractContentPath(meshObj.UvMapping.TexturePath);
250→        if (string.IsNullOrEmpty(contentPath))
251→        {
252→            Logger.Warning($"Skipping mesh '{meshObj.Name}' - invalid texture path: {meshObj.UvMapping.TexturePath}");
253→            return;
254→        }
255→
256→        // Load texture
257→        Texture2D texture;
258→        if (!_loadedTextures.TryGetValue(contentPath, out texture))
259→        {
260→            try
261→            {
262→                texture = _content.Load<Texture2D>(contentPath);
263→                _loadedTextures[contentPath] = texture;
264→                Logger.Debug($"Loaded texture: {contentPath}");
265→            }
266→            catch (Exception ex)
267→            {
268→                Logger.Error($"Failed to load texture '{contentPath}': {ex.Message}");
269→                return;
270→            }
271→        }
272→
273→        // Get UV coordinates from first layer
274→        var uvLayer = meshObj.UvMapping.UvLayers?.FirstOrDefault();
275→        if (uvLayer?.UvCoordinates == null || uvLayer.UvCoordinates.Count == 0)
276→        {
277→            Logger.Warning($"Skipping mesh '{meshObj.Name}' - no UV coordinates.");
278→            return;
279→        }
280→
281→        // Calculate source rectangle from UV coordinates
282→        var sourceRect = CalculateSourceRect(uvLayer.UvCoordinates, texture);
283→
284→        // Create entity
285→        var entity = _world.CreateEntity();
286→        _blenderEntities.Add(entity);
287→        _nameToEntity[meshObj.Name] = entity;
288→
289→        // Position from Blender
290→        var position = new Vector2(
291→            meshObj.Position?.X ?? 0,
292→            meshObj.Position?.Y ?? 0
293→        );
294→
295→        // Transform
296→        entity.Set(new Transform(position));
297→
298→        // SpriteInfo with calculated source rectangle
299→        // Use mesh dimensions from Blender for Size (already scaled), fallback to source rect if not available
300→        var size = new Vector2(
301→            meshObj.Dimensions?.X ?? sourceRect.Width,
302→            meshObj.Dimensions?.Y ?? sourceRect.Height
303→        );
304→
305→        // Calculate origin from Blender's origin offset
306→        // Blender origin offset is normalized (0-1) where (0,0) is bottom-left and (0.5,0.5) is center
307→        // SpriteBatch.Draw expects origin in SOURCE TEXTURE coordinates (not destination size)
308→        var originOffsetX = meshObj.OriginOffset?.X ?? 0.5f;  // Default to center
309→        var originOffsetY = meshObj.OriginOffset?.Y ?? 0.5f;
310→        // Y is inverted: Blender Y=0 is bottom, engine Y=0 is top
311→        // Use sourceRect dimensions since SpriteBatch.Draw origin is in source coordinates
312→        var origin = new Vector2(sourceRect.Width * originOffsetX, sourceRect.Height * (1 - originOffsetY));
313→
314→        entity.Set(new SpriteInfo
315→        {
316→            SpriteSheet = texture,
317→            Source = sourceRect,
318→            Size = size,
319→            Color = Color.White,
320→            Target = RenderTargetID.Main,
321→            LayerDepth = ResolveLayerDepth(meshObj, levelData),
322→            Origin = origin
323→        });
324→
325→        // DrawComponent
326→        entity.Set(new DrawComponent
327→        {
328→            Type = DrawElementType.Sprite,
329→            Target = RenderTargetID.Main
330→        });
331→
332→        // Make visible
333→        entity.Set(new Visible());
334→
335→        // Process collection-based components
336→        ProcessCollections(entity, meshObj, size);
337→
338→        Logger.Debug($"Created entity for mesh '{meshObj.Name}' at ({position.X}, {position.Y}) with size ({size.X}, {size.Y}), origin ({origin.X}, {origin.Y}), and source rect ({sourceRect.X}, {sourceRect.Y}, {sourceRect.Width}, {sourceRect.Height})");
339→    }
340→
341→    /// <summary>
342→    /// Resolves the layer depth for a mesh object using the DrawLayerMap (if set).
343→    /// Priority: object "drawLayer" custom property > collection name match (with hierarchy walk) > DEFAULT_LAYER_DEPTH.
344→    /// </summary>
345→    private float ResolveLayerDepth(BlenderObject meshObj, BlenderLevelData levelData)
346→    {
347→        if (_drawLayerMap == null)
348→            return DEFAULT_LAYER_DEPTH;
349→
350→        // 1. Check object's "drawLayer" custom property
351→        if (meshObj.CustomProperties?.TryGetValue("drawLayer", out var drawLayerValue) == true)
352→        {
353→            var layerName = drawLayerValue is JsonElement jsonEl
354→                ? jsonEl.GetString()
355→                : drawLayerValue?.ToString();
356→
357→            if (layerName != null && _drawLayerMap.TryGetDepth(layerName, out var depth))
358→                return depth;
359→        }
360→
361→        // 2. Try each Blender collection name, then walk up the collection hierarchy
362→        if (meshObj.Collections != null)
363→        {
364→            foreach (var collection in meshObj.Collections)
365→            {
366→                var current = collection;
367→                while (current != null)
368→                {
369→                    if (_drawLayerMap.TryGetDepth(current, out var depth))
370→                        return depth;
371→
372→                    // Walk up the hierarchy if available
373→                    if (levelData.CollectionHierarchy == null ||
374→                        !levelData.CollectionHierarchy.TryGetValue(current, out current))
375→                        break;
376→                }
377→            }
378→        }
379→
380→        // 3. Fall back to default
381→        return DEFAULT_LAYER_DEPTH;
382→    }
383→
384→    /// <summary>
385→    /// Extracts the MonoGame Content path from a full file system path.
386→    /// Finds "/Content/" and returns the substring after it, without file extension.
387→    /// </summary>
388→    private static string ExtractContentPath(string fullPath)
389→    {
390→        if (string.IsNullOrEmpty(fullPath))
391→            return null;
392→
393→        var contentIndex = fullPath.IndexOf("/Content/", StringComparison.OrdinalIgnoreCase);
394→        if (contentIndex < 0)
395→        {
396→            // Try Windows-style path
397→            contentIndex = fullPath.IndexOf("\\Content\\", StringComparison.OrdinalIgnoreCase);
398→        }
399→
400→        if (contentIndex < 0)
401→            return null;
402→
403→        var separatorLength = "/Content/".Length;
404→        var relativePath = fullPath.Substring(contentIndex + separatorLength);
405→
406→        // Remove file extension
407→        var extensionIndex = relativePath.LastIndexOf('.');
408→        if (extensionIndex > 0)
409→        {
410→            relativePath = relativePath.Substring(0, extensionIndex);
411→        }
412→
413→        return relativePath;
414→    }
415→
416→    /// <summary>
417→    /// Calculates a source rectangle from UV coordinates.
418→    /// Assumes a plane mesh with 4 vertices.
419→    /// </summary>
420→    private static Rectangle CalculateSourceRect(List<UvCoordinate> uvCoords, Texture2D texture)
421→    {
422→        if (uvCoords == null || uvCoords.Count == 0)
423→            return Rectangle.Empty;
424→
425→        float minU = uvCoords.Min(uv => uv.U);
426→        float maxU = uvCoords.Max(uv => uv.U);
427→        float minV = uvCoords.Min(uv => uv.V);
428→        float maxV = uvCoords.Max(uv => uv.V);
429→
430→        // Convert UV to pixel coordinates with rounding for better precision
431→        // UV origin is bottom-left, texture origin is top-left, so flip V
432→        int x = (int)Math.Round(minU * texture.Width);
433→        int y = (int)Math.Round((1 - maxV) * texture.Height);
434→        int width = (int)Math.Round((maxU - minU) * texture.Width);
435→        int height = (int)Math.Round((maxV - minV) * texture.Height);
436→
437→        return new Rectangle(x, y, width, height);
438→    }
439→
440→    /// <summary>
441→    /// Processes Blender collection membership and adds appropriate components.
442→    /// Collection names determine entity behavior (e.g., "Collision" adds BoxCollider).
443→    /// Collection custom properties can configure component settings.
444→    /// </summary>
445→    private void ProcessCollections(Entity entity, BlenderObject meshObj, Vector2 size)
446→    {
447→        if (meshObj.Collections == null || meshObj.Collections.Count == 0)
448→            return;
449→
450→        // Compute visual offset from origin so collision boxes align with rendered sprites.
451→        // SpriteBatch.Draw renders at: position - origin * scale
452→        // In destination space: offset = -(size * originOffset)
453→        var originOffsetX = meshObj.OriginOffset?.X ?? 0.5f;
454→        var originOffsetY = meshObj.OriginOffset?.Y ?? 0.5f;
455→        // Compute float edges of visual bounds relative to entity position
456→        float left = -size.X * originOffsetX;
457→        float top = -size.Y * (1 - originOffsetY);  // Y inverted: Blender Y=0 is bottom
458→        // Snap to smallest enclosing integer rectangle (floor expands left/top, ceiling expands right/bottom)
459→        int intLeft = (int)Math.Floor(left);
460→        int intTop = (int)Math.Floor(top);
461→        int intRight = (int)Math.Ceiling(left + size.X);
462→        int intBottom = (int)Math.Ceiling(top + size.Y);
463→
464→        var boundsOffset = new Point(intLeft, intTop);
465→        var boundsSize = new Point(intRight - intLeft, intBottom - intTop);
466→
467→        // Check for Collision collection
468→        if (meshObj.Collections.Contains("Collision"))
469→        {
470→            entity.Set(new EntityInfo("Collision", meshObj.Name));
471→
472→            // Get collision layer from collection properties (default to -1 for all layers)
473→            int layer = -1;
474→            bool passive = false;
475→
476→            if (meshObj.CollectionProperties?.TryGetValue("Collision", out var collisionProps) == true)
477→            {
478→                layer = GetIntProperty(collisionProps, "layer", -1);
479→                passive = GetBoolProperty(collisionProps, "passive", false);
480→            }
481→
482→            var bounds = new Rectangle(boundsOffset, boundsSize);
483→            var activeLayers = new HashSet<int> { layer };
484→            entity.Set(new BoxCollider(bounds, activeLayers, passive));
485→
486→            Logger.Debug($"Added BoxCollider to '{meshObj.Name}' (layer: {layer}, passive: {passive})");
487→        }
488→
489→        // Check for Player collection
490→        if (meshObj.Collections.Contains("Player"))
491→        {
492→            entity.Set(new EntityInfo("Player", meshObj.Name));
493→            entity.Set(new BoxCollider(new Rectangle(boundsOffset, boundsSize)));
494→            entity.Set(new RigidBody());
495→            entity.Set(new Velocity());
496→
497→            // Add camera follow target component
498→            entity.Set(new CameraFollowTarget
499→            {
500→                DampingX = 5.0f,
501→                DampingY = 5.0f,
502→                MaxDistanceX = 150.0f,
503→                MaxDistanceY = 100.0f,
504→                IsActive = true
505→            });
506→            Logger.Debug($"Set Player for '{meshObj.Name}'");
507→        }
508→
509→        // Check for Enemy collection
510→        if (meshObj.Collections.Contains("Enemy"))
511→        {
512→            entity.Set(new EntityInfo("Enemy", meshObj.Name));
513→            Logger.Debug($"Set Enemy for '{meshObj.Name}'");
514→        }
515→
516→        // Check for Trigger collection (collision but no physics push)
517→        if (meshObj.Collections.Contains("Trigger"))
518→        {
519→            if (!entity.Has<BoxCollider>())
520→            {
521→                var bounds = new Rectangle(boundsOffset, boundsSize);
522→                entity.Set(new BoxCollider(bounds, passive: true));
523→            }
524→            entity.Set(new EntityInfo("Trigger", meshObj.Name));
525→            Logger.Debug($"Set as Trigger zone for '{meshObj.Name}'");
526→        }
527→
528→        // Invoke registered game-specific collection handlers
529→        foreach (var collection in meshObj.Collections)
530→        {
531→            if (_collectionHandlers.TryGetValue(collection, out var handler))
532→            {
533→                handler(entity, meshObj);
534→            }
535→        }
536→    }
537→
538→    /// <summary>
539→    /// Applies a collider child's mesh vertices as a ConvexCollider on the parent entity.
540→    /// Replaces any existing BoxCollider (preserving its layer/passive settings).
541→    /// Sets YSortOffset on the parent's SpriteInfo and adds ColliderTag.
542→    /// </summary>
543→    private void ApplyColliderChild(string parentName, BlenderObject colliderObj)
544→    {
545→        if (!_nameToEntity.TryGetValue(parentName, out var parentEntity))
546→        {
547→            Logger.Warning($"Collider child '{colliderObj.Name}': parent entity '{parentName}' not found.");
548→            return;
549→        }
550→
551→        if (colliderObj.Vertices == null || colliderObj.Vertices.Count < 3)
552→        {
553→            Logger.Warning($"Collider child '{colliderObj.Name}': needs at least 3 vertices, got {colliderObj.Vertices?.Count ?? 0}.");
554→            return;
555→        }
556→
557→        // Build model vertices from exported data
558→        var modelVertices = new Vector2[colliderObj.Vertices.Count];
559→        for (var i = 0; i < colliderObj.Vertices.Count; i++)
560→        {
561→            modelVertices[i] = new Vector2(colliderObj.Vertices[i].X, colliderObj.Vertices[i].Y);
562→        }
563→
564→        // Guard against degenerate shapes (e.g. all vertices collinear or on the same axis)
565→        float minX = float.MaxValue, maxX = float.MinValue;
566→        float minY = float.MaxValue, maxY = float.MinValue;
567→        foreach (var v in modelVertices)
568→        {
569→            if (v.X < minX) minX = v.X;
570→            if (v.X > maxX) maxX = v.X;
571→            if (v.Y < minY) minY = v.Y;
572→            if (v.Y > maxY) maxY = v.Y;
573→        }
574→        if (maxX - minX < 0.01f || maxY - minY < 0.01f)
575→        {
576→            Logger.Warning($"Collider child '{colliderObj.Name}': degenerate shape (width={maxX - minX}, height={maxY - minY}), skipping.");
577→            return;
578→        }
579→
580→        // Determine collision layer and passive flag
581→        HashSet<int> activeLayers = null;
582→        bool passive = false;
583→
584→        // If parent already has a BoxCollider (from ProcessCollections), inherit its settings and remove it
585→        if (parentEntity.Has<BoxCollider>())
586→        {
587→            ref readonly var existingBox = ref parentEntity.Get<BoxCollider>();
588→            activeLayers = existingBox.ActiveLayers;
589→            passive = existingBox.Passive;
590→            parentEntity.Remove<BoxCollider>();
591→            Logger.Debug($"Replaced BoxCollider on '{parentName}' with ConvexCollider from '{colliderObj.Name}'.");
592→        }
593→        else
594→        {
595→            // Check collider child's own collection properties for collision settings
596→            if (colliderObj.CollectionProperties != null)
597→            {
598→                foreach (var (_, props) in colliderObj.CollectionProperties)
599→                {
600→                    var layer = GetIntProperty(props, "layer", -1);
601→                    passive = GetBoolProperty(props, "passive", false);
602→                    activeLayers = new HashSet<int> { layer };
603→                    break;
604→                }
605→            }
606→        }
607→
608→        // Create ConvexCollider with rotation ignored (baked into model vertices)
609→        parentEntity.Set(new ConvexCollider(modelVertices, activeLayers, passive, ignoreTransformRotation: true));
610→
611→        // Set YSortOffset = max Y of model vertices (bottom of collider in Y-down coords)
612→        // maxY was already computed in the degenerate-shape guard above
613→        if (parentEntity.Has<SpriteInfo>())
614→        {
615→            ref var spriteInfo = ref parentEntity.Get<SpriteInfo>();
616→            spriteInfo.YSortOffset = maxY;
617→        }
618→
619→        // Add ColliderTag
620→        parentEntity.Set(new ColliderTag());
621→
622→        Logger.Debug($"Applied ConvexCollider from '{colliderObj.Name}' to '{parentName}' ({modelVertices.Length} vertices, YSortOffset={maxY}).");
623→    }
624→
625→    /// <summary>
626→    /// Gets an integer property from collection properties dictionary.
627→    /// </summary>
628→    private static int GetIntProperty(Dictionary<string, object> props, string key, int defaultValue)
629→    {
630→        if (props == null || !props.TryGetValue(key, out var value))
631→            return defaultValue;
632→
633→        if (value is JsonElement jsonElement)
634→        {
635→            if (jsonElement.TryGetInt32(out var intVal))
636→                return intVal;
637→            if (jsonElement.TryGetDouble(out var doubleVal))
638→                return (int)doubleVal;
639→        }
640→
641→        if (value is int intValue)
642→            return intValue;
643→        if (value is double doubleValue)
644→            return (int)doubleValue;
645→        if (value is float floatValue)
646→            return (int)floatValue;
647→
648→        return defaultValue;
649→    }
650→
651→    /// <summary>
652→    /// Gets a boolean property from collection properties dictionary.
653→    /// </summary>
654→    private static bool GetBoolProperty(Dictionary<string, object> props, string key, bool defaultValue)
655→    {
656→        if (props == null || !props.TryGetValue(key, out var value))
657→            return defaultValue;
658→
659→        if (value is JsonElement jsonElement)
660→        {
661→            if (jsonElement.ValueKind == JsonValueKind.True)
662→                return true;
663→            if (jsonElement.ValueKind == JsonValueKind.False)
664→                return false;
665→        }
666→
667→        if (value is bool boolValue)
668→            return boolValue;
669→
670→        return defaultValue;
671→    }
672→
673→    private void CleanupEntities()
674→    {
675→        Logger.Info($"Cleaning up {_blenderEntities.Count} Blender entities...");
676→        foreach (var entity in _blenderEntities.ToArray())
677→        {
678→            if (entity.IsAlive)
679→            {
680→                entity.Dispose();
681→            }
682→        }
683→        _blenderEntities.Clear();
684→        _nameToEntity.Clear();
685→    }
686→
687→    public void Update(GameState state)
688→    {
689→        // Processing happens via message subscription
690→    }
691→
692→    public void Dispose()
693→    {
694→        CleanupEntities();
695→        _loadedTextures.Clear();
696→        GC.SuppressFinalize(this);
697→    }
698→}
