 2→namespace MonoDreams.Examples.Inspector;
 3→
 4→public class WatcherState
 5→{
 6→    private readonly HashSet<string> _checkedPaths = new();
 7→
 8→    public bool IsChecked(string leafPath) => _checkedPaths.Contains(leafPath);
 9→
10→    public void ToggleLeaf(string leafPath)
11→    {
12→        if (!_checkedPaths.Remove(leafPath))
13→            _checkedPaths.Add(leafPath);
14→    }
15→
16→    /// <summary>
17→    /// Toggle all leaves under a prefix. If any are checked, uncheck all; else check all.
18→    /// </summary>
19→    public void TogglePrefix(string prefix, List<string> allLeafPaths)
20→    {
21→        var anyChecked = allLeafPaths.Any(p => _checkedPaths.Contains(p));
22→        if (anyChecked)
23→        {
24→            foreach (var path in allLeafPaths)
25→                _checkedPaths.Remove(path);
26→        }
27→        else
28→        {
29→            foreach (var path in allLeafPaths)
30→                _checkedPaths.Add(path);
31→        }
32→    }
33→
34→    /// <summary>
35→    /// Returns tri-state: true = all checked, false = none checked, null = some checked (intermediate).
36→    /// </summary>
37→    public bool? GetPrefixState(string prefix, List<string> allLeafPaths)
38→    {
39→        if (allLeafPaths.Count == 0) return false;
40→
41→        var checkedCount = allLeafPaths.Count(p => _checkedPaths.Contains(p));
42→        if (checkedCount == 0) return false;
43→        if (checkedCount == allLeafPaths.Count) return true;
44→        return null; // intermediate
45→    }
46→
47→    public bool HasAnyChecked() => _checkedPaths.Count > 0;
48→
49→    public bool HasAnyCheckedUnder(string prefix, List<string> leafPaths)
50→    {
51→        return leafPaths.Any(p => _checkedPaths.Contains(p));
52→    }
53→
54→    public void Clear() => _checkedPaths.Clear();
55→}
56→#endif
