 7→namespace MonoDreams.Examples.Inspector;
 8→
 9→public class ComponentIntrospector : IComponentReader
10→{
11→    private readonly List<ComponentSnapshot> _accumulator = new();
12→
13→    private static readonly ConcurrentDictionary<Type, FieldInfo[]> FieldCache = new();
14→    private static readonly ConcurrentDictionary<Type, PropertyInfo[]> PropertyCache = new();
15→
16→    public void OnRead<T>(in T component, in Entity entity)
17→    {
18→        var type = typeof(T);
19→        var fields = FieldCache.GetOrAdd(type, t =>
20→            t.GetFields(BindingFlags.Public | BindingFlags.Instance));
21→        var properties = PropertyCache.GetOrAdd(type, t =>
22→            t.GetProperties(BindingFlags.Public | BindingFlags.Instance)
23→                .Where(p => p.CanRead && p.GetIndexParameters().Length == 0)
24→                .ToArray());
25→
26→        _accumulator.Add(new ComponentSnapshot
27→        {
28→            TypeName = type.Name,
29→            Type = type,
30→            BoxedValue = component,
31→            Fields = fields,
32→            Properties = properties
33→        });
34→    }
35→
36→    public List<ComponentSnapshot> ReadEntity(Entity entity)
37→    {
38→        _accumulator.Clear();
39→        entity.ReadAllComponents(this);
40→        return new List<ComponentSnapshot>(_accumulator);
41→    }
42→}
