15→/// Runs after SpritePrepSystem (which sets LayerDepth from SpriteInfo) and overwrites
16→/// LayerDepth with the Y-interpolated value for participating entities.
17→/// </summary>
18→[With(typeof(DrawComponent), typeof(SpriteInfo), typeof(Transform), typeof(Visible))]
19→public class YSortSystem(World world, MonoDreams.Component.Camera camera, DrawLayerMap drawLayerMap)
20→    : AEntitySetSystem<GameState>(world)
21→{
22→    private float _boundsTop;
23→    private float _boundsHeight;
24→
25→    protected override void PreUpdate(GameState state)
26→    {
27→        var bounds = camera.VirtualScreenBounds;
28→        _boundsTop = bounds.Top;
29→        _boundsHeight = bounds.Height;
30→    }
31→
32→    protected override void Update(GameState state, in Entity entity)
33→    {
34→        ref readonly var spriteInfo = ref entity.Get<SpriteInfo>();
35→
36→        if (!drawLayerMap.TryGetYSortRange(spriteInfo.LayerDepth, out var minDepth, out var maxDepth))
37→            return;
38→
39→        ref readonly var transform = ref entity.Get<Transform>();
40→        ref var drawComponent = ref entity.Get<DrawComponent>();
41→
42→        // Normalize Y within camera bounds: 0 = top, 1 = bottom
43→        float t = _boundsHeight > 0
44→            ? Math.Clamp((transform.WorldPosition.Y + spriteInfo.YSortOffset - _boundsTop) / _boundsHeight, 0f, 1f)
45→            : 0.5f;
46→
47→        // Higher t (lower on screen) → higher depth → rendered in front
48→        drawComponent.LayerDepth = Math.Clamp(
