 13→namespace MonoDreams.Examples.Inspector;
 14→
 15→public class DebugInspector
 16→{
 17→    private bool _visible;
 18→    private KeyboardState _previousKeyboardState;
 19→    private readonly ComponentIntrospector _introspector = new();
 20→    private List<EntitySnapshot> _rootSnapshots = new();
 21→    private Dictionary<Entity, EntitySnapshot> _allSnapshots = new();
 22→    private int _totalEntityCount;
 23→    private int _rootCount;
 24→    private readonly WatcherState _watcherState = new();
 25→    private World _lastWorld;
 26→    private int _frameCounter;
 27→    private string _filterText = "";
 28→    private const int ScanInterval = 15; // ~4x/sec at 60fps
 29→    private int _nextNodeId;
 30→
 31→    public bool WantsKeyboard => _visible && ImGui.GetIO().WantCaptureKeyboard;
 32→    public bool WantsMouse => _visible && ImGui.GetIO().WantCaptureMouse;
 33→
 34→    public void HandleInput()
 35→    {
 36→        var currentState = Keyboard.GetState();
 37→        if (currentState.IsKeyDown(Keys.F1) && _previousKeyboardState.IsKeyUp(Keys.F1))
 38→        {
 39→            _visible = !_visible;
 40→        }
 41→        _previousKeyboardState = currentState;
 42→    }
 43→
 44→    public void Draw(World world)
 45→    {
 46→        if (!_visible || world == null) return;
 47→
 48→        // Detect world change (screen transition)
 49→        if (world != _lastWorld)
 50→        {
 51→            _rootSnapshots.Clear();
 52→            _allSnapshots.Clear();
 53→            _watcherState.Clear();
 54→            _lastWorld = world;
 55→            _frameCounter = 0;
 56→        }
 57→
 58→        // Throttled entity scan
 59→        if (_frameCounter++ % ScanInterval == 0)
 60→        {
 61→            RefreshEntitySnapshots(world);
 62→        }
 63→
 64→        DrawWorldHierarchyPanel();
 65→        DrawWatchersPanel();
 66→    }
 67→
 68→    private void RefreshEntitySnapshots(World world)
 69→    {
 70→        _allSnapshots.Clear();
 71→        _rootSnapshots.Clear();
 72→        _totalEntityCount = 0;
 73→
 74→        // Build snapshot for every entity
 75→        foreach (var entity in world.GetEntities().AsEnumerable())
 76→        {
 77→            if (!entity.IsAlive) continue;
 78→            _totalEntityCount++;
 79→
 80→            string displayName;
 81→            if (entity.Has<EntityInfo>())
 82→            {
 83→                var info = entity.Get<EntityInfo>();
 84→                displayName = info.Name != null
 85→                    ? $"{info.Name} ({info.Type}) #{entity.GetHashCode():X}"
 86→                    : $"{info.Type} #{entity.GetHashCode():X}";
 87→            }
 88→            else
 89→            {
 90→                displayName = $"Entity #{entity.GetHashCode():X}";
 91→            }
 92→
 93→            _allSnapshots[entity] = new EntitySnapshot
 94→            {
 95→                Entity = entity,
 96→                DisplayName = displayName,
 97→                Components = _introspector.ReadEntity(entity)
 98→            };
 99→        }
100→
101→        // Read hierarchy from world resource (set by HierarchySystem)
102→        var hierarchy = world.Has<EntityHierarchy>() ? world.Get<EntityHierarchy>() : null;
103→
104→        if (hierarchy != null)
105→        {
106→            // Wire up parent-child snapshot tree
107→            foreach (var (entity, snapshot) in _allSnapshots)
108→            {
109→                var parent = hierarchy.GetParent(entity);
110→                if (parent.HasValue && _allSnapshots.TryGetValue(parent.Value, out var parentSnapshot))
111→                {
112→                    parentSnapshot.Children.Add(snapshot);
113→                }
114→                else
115→                {
116→                    _rootSnapshots.Add(snapshot);
117→                }
118→            }
119→        }
120→        else
121→        {
122→            // No hierarchy available — flat list
123→            _rootSnapshots.AddRange(_allSnapshots.Values);
124→        }
125→
126→        _rootCount = _rootSnapshots.Count;
127→    }
128→
129→    private void DrawWorldHierarchyPanel()
130→    {
131→        ImGui.Begin("World Hierarchy");
132→        ImGui.Text($"Entities: {_totalEntityCount} ({_rootCount} roots)");
133→        ImGui.InputTextWithHint("##filter", "Filter...", ref _filterText, 256);
134→        ImGui.Separator();
135→
136→        _nextNodeId = 0;
137→        for (var i = 0; i < _rootSnapshots.Count; i++)
138→        {
139→            DrawEntityNode(_rootSnapshots[i]);
140→        }
141→
142→        ImGui.End();
143→    }
144→
145→    private void DrawEntityNode(EntitySnapshot snapshot)
146→    {
147→        var nodeId = _nextNodeId++;
148→
149→        var hasFilter = _filterText.Length > 0;
150→
151→        // When filtering, check if this entity or any descendant matches
152→        if (hasFilter && !MatchesFilter(snapshot))
153→            return;
154→
155→        var entityPath = $"Entity:{snapshot.DisplayName}";
156→        var hasChildren = snapshot.Children.Count > 0;
157→
158→        DrawTriStateCheckbox(entityPath, snapshot);
159→        ImGui.SameLine();
160→
161→        var flags = hasChildren ? ImGuiTreeNodeFlags.None : ImGuiTreeNodeFlags.Leaf;
162→        if (ImGui.TreeNodeEx($"{snapshot.DisplayName}##e{nodeId}", flags))
163→        {
164→            // Draw components
165→            for (var c = 0; c < snapshot.Components.Count; c++)
166→            {
167→                var comp = snapshot.Components[c];
168→                var compPath = $"{entityPath}/{comp.TypeName}";
169→                DrawTriStateCheckbox(compPath, snapshot, comp);
170→                ImGui.SameLine();
171→
172→                if (ImGui.TreeNode($"{comp.TypeName}##e{nodeId}c{c}"))
173→                {
174→                    DrawComponentFields(comp, compPath);
175→                    ImGui.TreePop();
176→                }
177→            }
178→
179→            // Draw children recursively
180→            for (var i = 0; i < snapshot.Children.Count; i++)
181→            {
182→                DrawEntityNode(snapshot.Children[i]);
183→            }
184→
185→            ImGui.TreePop();
186→        }
187→    }
188→
189→    private bool MatchesFilter(EntitySnapshot snapshot)
190→    {
191→        if (snapshot.DisplayName.Contains(_filterText, StringComparison.OrdinalIgnoreCase))
192→            return true;
193→
194→        foreach (var child in snapshot.Children)
195→        {
196→            if (MatchesFilter(child))
197→                return true;
198→        }
199→
200→        return false;
201→    }
202→
203→    private void DrawComponentFields(ComponentSnapshot comp, string compPath)
204→    {
205→        foreach (var field in comp.Fields)
206→        {
207→            var fieldPath = $"{compPath}/{field.Name}";
208→            DrawLeafCheckbox(fieldPath);
209→            ImGui.SameLine();
210→
211→            var value = field.GetValue(comp.BoxedValue);
212→            DrawFormattedValue(field.Name, value, fieldPath);
213→        }
214→        foreach (var prop in comp.Properties)
215→        {
216→            var fieldPath = $"{compPath}/{prop.Name}";
217→            DrawLeafCheckbox(fieldPath);
218→            ImGui.SameLine();
219→
220→            try
221→            {
222→                var value = prop.GetValue(comp.BoxedValue);
223→                DrawFormattedValue(prop.Name, value, fieldPath);
224→            }
225→            catch
226→            {
227→                ImGui.Text($"{prop.Name}: <error>");
228→            }
229→        }
230→    }
231→
232→    private static void DrawFormattedValue(string name, object value, string id)
233→    {
234→        if (value is XnaColor c)
235→        {
236→            ImGui.Text($"{name}:");
237→            ImGui.SameLine();
238→            var colorVec = new Vector4(c.R / 255f, c.G / 255f, c.B / 255f, c.A / 255f);
239→            ImGui.ColorButton($"##color{id}", colorVec, ImGuiColorEditFlags.None, new Vector2(14, 14));
240→            ImGui.SameLine();
241→            ImGui.Text($"({c.R}, {c.G}, {c.B}, {c.A})");
242→        }
243→        else
244→        {
245→            ImGui.Text($"{name}: {FormatValue(value)}");
246→        }
247→    }
248→
249→    private static string FormatValue(object value)
250→    {
251→        if (value == null) return "null";
252→        return value switch
253→        {
254→            XnaVector2 v => $"({v.X:F1}, {v.Y:F1})",
255→            XnaVector3 v => $"({v.X:F1}, {v.Y:F1}, {v.Z:F1})",
256→            XnaRectangle r => $"[{r.X}, {r.Y}, {r.Width}x{r.Height}]",
257→            XnaColor c => $"({c.R}, {c.G}, {c.B}, {c.A})",
258→            bool b => b ? "true" : "false",
259→            float f => f.ToString("F2"),
260→            double d => d.ToString("F2"),
261→            Enum e => e.ToString(),
262→            string s => $"\"{s}\"",
263→            _ => value.ToString() ?? "null"
264→        };
265→    }
266→
267→    private void DrawTriStateCheckbox(string prefix, EntitySnapshot snapshot, ComponentSnapshot comp = null)
268→    {
269→        var leafPaths = GetLeafPaths(prefix, snapshot, comp);
270→        var state = _watcherState.GetPrefixState(prefix, leafPaths);
271→
272→        // ImGui doesn't have native tri-state: we render it manually
273→        var isChecked = state == true;
274→        if (ImGui.Checkbox($"##chk{prefix}", ref isChecked))
275→        {
276→            _watcherState.TogglePrefix(prefix, leafPaths);
277→        }
278→
279→        // Draw intermediate dash overlay
280→        if (state == null) // some checked
281→        {
282→            var drawList = ImGui.GetWindowDrawList();
283→            var min = ImGui.GetItemRectMin();
284→            var max = ImGui.GetItemRectMax();
285→            var midY = (min.Y + max.Y) * 0.5f;
286→            var dashColor = ImGui.GetColorU32(ImGuiCol.CheckMark);
287→            drawList.AddLine(
288→                new Vector2(min.X + 4, midY),
289→                new Vector2(max.X - 4, midY),
290→                dashColor, 2f);
291→        }
292→    }
293→
294→    private void DrawLeafCheckbox(string leafPath)
295→    {
296→        var isChecked = _watcherState.IsChecked(leafPath);
297→        if (ImGui.Checkbox($"##chk{leafPath}", ref isChecked))
298→        {
299→            _watcherState.ToggleLeaf(leafPath);
300→        }
