303→    private static List<string> GetLeafPaths(string prefix, EntitySnapshot snapshot, ComponentSnapshot comp)
304→    {
305→        var paths = new List<string>();
306→        if (comp != null)
307→        {
308→            foreach (var field in comp.Fields)
