310→            foreach (var prop in comp.Properties)
311→                paths.Add($"{prefix}/{prop.Name}");
312→        }
313→        else
314→        {
315→            foreach (var c in snapshot.Components)
316→            {
317→                var compPath = $"{prefix}/{c.TypeName}";
318→                foreach (var field in c.Fields)
319→                    paths.Add($"{compPath}/{field.Name}");
320→                foreach (var prop in c.Properties)
321→                    paths.Add($"{compPath}/{prop.Name}");
322→            }
