232→    private static void DrawFormattedValue(string name, object value, string id)
233→    {
234→        if (value is XnaColor c)
235→        {
236→            ImGui.Text($"{name}:");
237→            ImGui.SameLine();
238→            var colorVec = new Vector4(c.R / 255f, c.G / 255f, c.B / 255f, c.A / 255f);
239→            ImGui.ColorButton($"##color{id}", colorVec, ImGuiColorEditFlags.None, new Vector2(14, 14));
240→            ImGui.SameLine();
241→            ImGui.Text($"({c.R}, {c.G}, {c.B}, {c.A})");
242→        }
243→        else
244→        {
