272→        // ImGui doesn't have native tri-state: we render it manually
273→        var isChecked = state == true;
274→        if (ImGui.Checkbox($"##chk{prefix}", ref isChecked))
275→        {
276→            _watcherState.TogglePrefix(prefix, leafPaths);
277→        }
278→
279→        // Draw intermediate dash overlay
280→        if (state == null) // some checked
281→        {
282→            var drawList = ImGui.GetWindowDrawList();
283→            var min = ImGui.GetItemRectMin();
284→            var max = ImGui.GetItemRectMax();
285→            var midY = (min.Y + max.Y) * 0.5f;
286→            var dashColor = ImGui.GetColorU32(ImGuiCol.CheckMark);
287→            drawList.AddLine(
288→                new Vector2(min.X + 4, midY),
289→                new Vector2(max.X - 4, midY),
290→                dashColor, 2f);
291→        }
292→    }
