360→                    var compLeafPaths = GetLeafPaths(compPath, snapshot, comp);
361→
362→                    if (!_watcherState.HasAnyCheckedUnder(compPath, compLeafPaths))
363→                        continue;
364→
365→                    DrawTriStateCheckbox(compPath, snapshot, comp);
366→                    ImGui.SameLine();
367→
