            if (!_watcherState.IsChecked(fieldPath)) continue;

            DrawLeafCheckbox(fieldPath);
            ImGui.SameLine();
            try
            {
                var value = prop.GetValue(comp.BoxedValue);
                DrawFormattedValue(prop.Name, value, $"w{fieldPath}");
            }
            catch
            {
                ImGui.Text($"{prop.Name}: <error>");
            }
        }
    }
}
