128→    private SequentialSystem<GameState> CreateUpdateSystem()
129→    {
130→        var debugDir = Environment.GetEnvironmentVariable("MONODREAMS_DEBUG_DIR")
131→            ?? Path.Combine(AppDomain.CurrentDomain.BaseDirectory, "debug");
132→        var inputMappingSystem = new InputMappingSystem(_world);
133→#if DEBUG
134→        _inputMappingSystem = inputMappingSystem;
135→#endif
136→        var actionMap = new Dictionary<string, AInputState>
137→        {
138→            ["Up"] = InputState.Up, ["Down"] = InputState.Down,
139→            ["Left"] = InputState.Left, ["Right"] = InputState.Right,
140→            ["Jump"] = InputState.Jump, ["Grab"] = InputState.Grab,
141→            ["Orb"] = InputState.Orb, ["Exit"] = InputState.Exit,
142→            ["Interact"] = InputState.Interact,
143→        };
144→
145→        var replaySystem = InputReplaySystem.TryLoad(debugDir, actionMap, _game);
146→
147→        ISystem<GameState> inputSystems;
148→        if (replaySystem != null)
149→        {
150→            inputMappingSystem.SkipHardwareRead = true;
151→            inputSystems = new SequentialSystem<GameState>(
152→                new CursorInputSystem(_world), replaySystem, inputMappingSystem);
153→        }
154→        else
155→        {
156→            inputSystems = new ParallelSystem<GameState>(_parallelRunner,
157→                new CursorInputSystem(_world), inputMappingSystem);
158→        }
159→        
160→        var promptFont = _content.Load<BitmapFont>("Fonts/PPMondwest-Regular-fnt");
161→
162→        var blenderParser = new BlenderLevelParserSystem(_world, _content, _camera);
163→        blenderParser.SetDrawLayerMap(_layers);
164→        blenderParser.RegisterCollectionHandler("Player", (entity, _) =>
165→        {
166→            entity.Set(new PlayerState());
167→            entity.Set(new StopMotionEffect { OffsetRadians = MathHelper.ToRadians(2f) });
168→        });
169→        blenderParser.RegisterCollectionHandler("NPC", (entity, blenderObj) =>
170→        {
171→            var npcName = blenderObj.Name;
172→            entity.Set(new EntityInfo("NPC", npcName));
173→
174→            // EMPTY objects (parent empties in hierarchy) don't have sprites — skip visual setup
175→            if (!entity.Has<SpriteInfo>()) return;
176→
177→            var isSilhouette = blenderObj.Name.EndsWith("shilhouette");
178→
179→            // Silhouette entities rotate counterclockwise; compensate for parent's +2° with -4° local
180→            var offset = isSilhouette
181→                ? MathHelper.ToRadians(-4f)
182→                : MathHelper.ToRadians(2f);
183→            entity.Set(new StopMotionEffect { OffsetRadians = offset });
184→
185→            // Silhouette renders behind parent: negative bias = lower depth = further back
186→            if (isSilhouette)
187→            {
188→                ref var sprite = ref entity.Get<SpriteInfo>();
189→                sprite.YSortDepthBias = -0.0005f;
190→            }
191→
192→            var npcTransform = entity.Get<Transform>();
193→            var npcSprite = entity.Get<SpriteInfo>();
194→            var npcDimensions = new Vector2(
195→                blenderObj.Dimensions?.X ?? npcSprite.Size.X,
196→                blenderObj.Dimensions?.Y ?? npcSprite.Size.Y);
197→
198→            // Create interaction zone entity (wider than the NPC for approach detection)
199→            var zoneEntity = _world.CreateEntity();
200→            zoneEntity.Set(new EntityInfo("NPCZone"));
201→            zoneEntity.Set(new Transform());
202→            zoneEntity.SetParent(entity);
203→
204→            var zoneWidth = (int)(npcDimensions.X * 2.5f);
205→            var zoneHeight = (int)(npcDimensions.Y * 1.5f);
206→            var zoneBounds = new Rectangle(-zoneWidth / 2, -zoneHeight / 2, zoneWidth, zoneHeight);
207→            zoneEntity.Set(new BoxCollider(zoneBounds, passive: true));
208→            zoneEntity.Set(new DialogueZoneComponent(npcName, oneTimeOnly: false, autoStart: false, npcName: npcName));
209→
210→            // Create floating icon entity (above the NPC sprite)
211→            var iconEntity = _world.CreateEntity();
212→            iconEntity.Set(new EntityInfo("InteractionIcon", $"{npcName}Icon"));
213→
214→            // Compute icon position above NPC visual top
215→            var originOffsetY = blenderObj.OriginOffset?.Y ?? 0.5f;
216→            var visualTop = -npcDimensions.Y * (1 - originOffsetY);
217→            var iconOffset = new Vector2(0, visualTop - 6f);
218→
219→            iconEntity.Set(new Transform(iconOffset));
220→            iconEntity.SetParent(entity);
221→            iconEntity.Set(new DynamicText
222→            {
223→                Target = RenderTargetID.Main,
224→                Font = promptFont,
225→                TextContent = "E",
226→                Color = Color.White,
227→                Scale = 0.4f,
228→                LayerDepth = _layers.GetDepth(GameDrawLayer.Characters, -0.01f),
229→                IsRevealed = true,
230→                VisibleCharacterCount = 1,
231→                RevealingSpeed = 0,
232→                RevealStartTime = 0
233→            });
234→            iconEntity.Set(new DrawComponent
235→            {
236→                Type = DrawElementType.Text,
237→                Target = RenderTargetID.Main
238→            });
239→            // No Visible initially — managed by NPCInteractionSystem
240→
241→            zoneEntity.Set(new NPCInteractionIcon { IconEntity = iconEntity });
242→        });
243→
244→        var entitySpawnSystem = new EntitySpawnSystem(_world, _content, _renderTargets);
245→        entitySpawnSystem.RegisterEntityFactory("Tile", new TileEntityFactory(_layers));
246→        entitySpawnSystem.RegisterEntityFactory("Wall", new WallEntityFactory(_content, _layers));
247→        entitySpawnSystem.RegisterEntityFactory("Player", new PlayerEntityFactory(_content, _layers));
248→        entitySpawnSystem.RegisterEntityFactory("Enemy", new NPCEntityFactory(_content, _layers));
249→
250→        var levelLoadSystems = new SequentialSystem<GameState>(
251→            new LevelLoadRequestSystem(_world, _content),
252→            blenderParser,
253→            new LDtkTileParserSystem(_world, _content),
254→            new LDtkEntityParserSystem(_world),
255→            entitySpawnSystem);
256→        
257→        // Collision pipeline must run sequentially (movement → velocity → detect → resolve → commit)
258→        // Individual systems keep their internal _parallelRunner for entity-level parallelism
259→        var logicSystems = new SequentialSystem<GameState>(
260→            new MovementSystem(_world, _parallelRunner),
261→            new OrbSystem(_world),
262→            new StopMotionEffectSystem(_world),
263→            new TransformVelocitySystem(_world, _parallelRunner),
264→            new TransformCollisionDetectionSystem<CollisionMessage>(_world, _parallelRunner, GameCollisionHelper.Create),
265→            new TransformPhysicalCollisionResolutionSystem(_world),
266→            new TransformCommitSystem(_world, _parallelRunner),
267→            new TextUpdateSystem(_world), // Logic only
268→            new NPCInteractionSystem(_world),
269→            new DialogueSystem(_world, _content, _graphicsDevice, _viewportManager.VirtualWidth, _viewportManager.VirtualHeight, _layers,
270→                new[] { "Dialogues/hello_world", "Dialogues/boldo" })
271→            // ... other game logic systems
272→        );
273→        
274→        // Hierarchy system must run AFTER logic systems modify transforms
275→        // but BEFORE any systems read world transforms (camera, rendering, etc.)
276→        var hierarchySystem = new HierarchySystem(_world);
277→
278→        var cameraFollowSystem = new CameraFollowSystem(_world, _camera);
279→
280→        // Cursor position must update AFTER camera has moved to avoid 1-frame lag
281→        var cursorLateUpdateSystem = new CursorPositionSystem(_world, _camera, _viewportManager);
282→
283→        // var debugSystems = new ParallelSystem<GameState>(_parallelRunner,
284→        //     new ColliderDebugSystem(_world, _graphicsDevice)
285→        // );
286→
287→        return new SequentialSystem<GameState>(
288→            // new DebugSystem(_world, _game, _spriteBatch), // If needed
289→            inputSystems,
290→            levelLoadSystems,
291→            logicSystems,
292→            hierarchySystem, // Entity hierarchy + transform dirty flag propagation
293→            cameraFollowSystem,
294→            cursorLateUpdateSystem,          // Cursor position updates after camera
295→            new CursorDrawPrepSystem(_world) // Draw prep after position is finalized
296→            // debugSystems
297→        );
298→    }
299→    
300→    private SequentialSystem<GameState> CreateDrawSystem()
301→    {
302→        var pixelPerfectRendering = SettingsManager.Instance.Settings.PixelPerfectRendering;
303→
304→        // Systems that prepare DrawComponent based on state (can often be parallel)
305→        var prepDrawSystems = new SequentialSystem<GameState>( // Or parallel if clearing is handled carefully
306→            // Optional: A system to clear all DrawComponents first?
307→            // new ClearDrawComponentSystem(_world),
308→            new CullingSystem(_world, _camera),
309→            new SpritePrepSystem(_world, _graphicsDevice, pixelPerfectRendering),
310→            new YSortSystem(_world, _camera, _layers),
311→            new TextPrepSystem(_world, pixelPerfectRendering),
312→            new MeshPrepSystem(_world)
313→            // new SpriteDebugSystem(_world)  // Debug visualization for sprite bounds and origins
314→            // ... other systems preparing DrawElements (UI, particles, etc.)
315→        );
316→
317→        // The single system that handles all rendering (strictly sequential)
318→        var renderSystem = new MasterRenderSystem(
319→            _spriteBatch,
320→            _graphicsDevice,
321→            _camera,
322→            _renderTargets, // Pass the dictionary/collection of RTs
323→            _world
324→        );
325→    
326→        // Final system to draw RenderTargets to backbuffer (if needed)
327→        var finalDrawToScreenSystem = new FinalDrawSystem(_spriteBatch, _graphicsDevice, _viewportManager, _camera, _renderTargets);
328→
329→        var debugDir = Environment.GetEnvironmentVariable("MONODREAMS_DEBUG_DIR")
330→            ?? Path.Combine(AppDomain.CurrentDomain.BaseDirectory, "debug");
331→        var replayPlan = InputReplayPlan.TryLoad(debugDir);
332→        var screenshotSystem = new ScreenshotCaptureSystem(_graphicsDevice, captureIntervalSeconds: 2.0f, debugDir)
333→        {
334→            IsEnabled = replayPlan?.Screenshots ?? false
335→        };
336→
337→        return new SequentialSystem<GameState>(
338→            prepDrawSystems,
339→            renderSystem,
340→            finalDrawToScreenSystem, // Draw RTs to screen
341→            screenshotSystem
342→        );
343→    }
344→
345→    public void Dispose()
346→    {
347→        UpdateSystem.Dispose();
348→        GC.SuppressFinalize(this);
349→    }
350→}

