261→    private void ProcessMesh(BlenderObject meshObj, BlenderLevelData levelData)
262→    {
263→        if (meshObj.UvMapping == null)
264→        {
265→            Logger.Warning($"Skipping mesh '{meshObj.Name}' - no UV mapping.");
266→            return;
267→        }
268→
269→        // Extract content path from texture path
270→        var contentPath = ExtractContentPath(meshObj.UvMapping.TexturePath);
271→        if (string.IsNullOrEmpty(contentPath))
272→        {
273→            Logger.Warning($"Skipping mesh '{meshObj.Name}' - invalid texture path: {meshObj.UvMapping.TexturePath}");
274→            return;
275→        }
276→
277→        // Load texture
278→        Texture2D texture;
279→        if (!_loadedTextures.TryGetValue(contentPath, out texture))
280→        {
281→            try
282→            {
283→                texture = _content.Load<Texture2D>(contentPath);
284→                _loadedTextures[contentPath] = texture;
285→                Logger.Debug($"Loaded texture: {contentPath}");
286→            }
287→            catch (Exception ex)
288→            {
289→                Logger.Error($"Failed to load texture '{contentPath}': {ex.Message}");
290→                return;
291→            }
292→        }
293→
294→        // Get UV coordinates from first layer
295→        var uvLayer = meshObj.UvMapping.UvLayers?.FirstOrDefault();
296→        if (uvLayer?.UvCoordinates == null || uvLayer.UvCoordinates.Count == 0)
297→        {
298→            Logger.Warning($"Skipping mesh '{meshObj.Name}' - no UV coordinates.");
299→            return;
300→        }
301→
302→        // Calculate source rectangle from UV coordinates
303→        var sourceRect = CalculateSourceRect(uvLayer.UvCoordinates, texture);
304→
305→        // Create entity
306→        var entity = _world.CreateEntity();
307→        _blenderEntities.Add(entity);
308→        _nameToEntity[meshObj.Name] = entity;
309→
310→        // Position from Blender
311→        var position = new Vector2(
312→            meshObj.Position?.X ?? 0,
313→            meshObj.Position?.Y ?? 0
314→        );
315→
316→        // Transform
317→        entity.Set(new Transform(position));
318→
319→        // SpriteInfo with calculated source rectangle
320→        // Use mesh dimensions from Blender for Size (already scaled), fallback to source rect if not available
321→        var size = new Vector2(
322→            meshObj.Dimensions?.X ?? sourceRect.Width,
323→            meshObj.Dimensions?.Y ?? sourceRect.Height
324→        );
325→
326→        // Calculate origin from Blender's origin offset
327→        // Blender origin offset is normalized (0-1) where (0,0) is bottom-left and (0.5,0.5) is center
328→        // SpriteBatch.Draw expects origin in SOURCE TEXTURE coordinates (not destination size)
329→        var originOffsetX = meshObj.OriginOffset?.X ?? 0.5f;  // Default to center
330→        var originOffsetY = meshObj.OriginOffset?.Y ?? 0.5f;
331→        // Y is inverted: Blender Y=0 is bottom, engine Y=0 is top
332→        // Use sourceRect dimensions since SpriteBatch.Draw origin is in source coordinates
333→        var origin = new Vector2(sourceRect.Width * originOffsetX, sourceRect.Height * (1 - originOffsetY));
334→
335→        entity.Set(new SpriteInfo
336→        {
337→            SpriteSheet = texture,
338→            Source = sourceRect,
339→            Size = size,
340→            Color = Color.White,
341→            Target = RenderTargetID.Main,
342→            LayerDepth = ResolveLayerDepth(meshObj, levelData),
343→            Origin = origin
344→        });
345→
346→        // DrawComponent
347→        entity.Set(new DrawComponent
348→        {
349→            Type = DrawElementType.Sprite,
350→            Target = RenderTargetID.Main
351→        });
352→
353→        // Make visible
354→        entity.Set(new Visible());
355→
356→        // Process collection-based components
357→        ProcessCollections(entity, meshObj, size);
358→
359→        Logger.Debug($"Created entity for mesh '{meshObj.Name}' at ({position.X}, {position.Y}) with size ({size.X}, {size.Y}), origin ({origin.X}, {origin.Y}), and source rect ({sourceRect.X}, {sourceRect.Y}, {sourceRect.Width}, {sourceRect.Height})");
360→    }
