362→    /// <summary>
363→    /// Resolves the layer depth for a mesh object using the DrawLayerMap (if set).
364→    /// Priority: object "drawLayer" custom property > collection name match (with hierarchy walk) > DEFAULT_LAYER_DEPTH.
365→    /// </summary>
366→    private float ResolveLayerDepth(BlenderObject meshObj, BlenderLevelData levelData)
367→    {
368→        if (_drawLayerMap == null)
369→            return DEFAULT_LAYER_DEPTH;
370→
371→        // 1. Check object's "drawLayer" custom property
372→        if (meshObj.CustomProperties?.TryGetValue("drawLayer", out var drawLayerValue) == true)
373→        {
374→            var layerName = drawLayerValue is JsonElement jsonEl
375→                ? jsonEl.GetString()
376→                : drawLayerValue?.ToString();
377→
378→            if (layerName != null && _drawLayerMap.TryGetDepth(layerName, out var depth))
379→                return depth;
380→        }
381→
382→        // 2. Try each Blender collection name, then walk up the collection hierarchy
383→        if (meshObj.Collections != null)
384→        {
385→            foreach (var collection in meshObj.Collections)
386→            {
387→                var current = collection;
388→                while (current != null)
389→                {
390→                    if (_drawLayerMap.TryGetDepth(current, out var depth))
391→                        return depth;
392→
393→                    // Walk up the hierarchy if available
394→                    if (levelData.CollectionHierarchy == null ||
395→                        !levelData.CollectionHierarchy.TryGetValue(current, out current))
396→                        break;
397→                }
398→            }
399→        }
400→
401→        // 3. Fall back to default
402→        return DEFAULT_LAYER_DEPTH;
403→    }
