405→    /// <summary>
406→    /// Extracts the MonoGame Content path from a full file system path.
407→    /// Finds "/Content/" and returns the substring after it, without file extension.
408→    /// </summary>
409→    private static string ExtractContentPath(string fullPath)
410→    {
411→        if (string.IsNullOrEmpty(fullPath))
412→            return null;
413→
414→        var contentIndex = fullPath.IndexOf("/Content/", StringComparison.OrdinalIgnoreCase);
415→        if (contentIndex < 0)
416→        {
417→            // Try Windows-style path
418→            contentIndex = fullPath.IndexOf("\\Content\\", StringComparison.OrdinalIgnoreCase);
419→        }
420→
421→        if (contentIndex < 0)
422→            return null;
423→
424→        var separatorLength = "/Content/".Length;
425→        var relativePath = fullPath.Substring(contentIndex + separatorLength);
426→
427→        // Remove file extension
428→        var extensionIndex = relativePath.LastIndexOf('.');
429→        if (extensionIndex > 0)
430→        {
431→            relativePath = relativePath.Substring(0, extensionIndex);
432→        }
433→
434→        return relativePath;
435→    }
