437→    /// <summary>
438→    /// Calculates a source rectangle from UV coordinates.
439→    /// Assumes a plane mesh with 4 vertices.
440→    /// </summary>
441→    private static Rectangle CalculateSourceRect(List<UvCoordinate> uvCoords, Texture2D texture)
442→    {
443→        if (uvCoords == null || uvCoords.Count == 0)
444→            return Rectangle.Empty;
445→
446→        float minU = uvCoords.Min(uv => uv.U);
447→        float maxU = uvCoords.Max(uv => uv.U);
448→        float minV = uvCoords.Min(uv => uv.V);
449→        float maxV = uvCoords.Max(uv => uv.V);
450→
451→        // Convert UV to pixel coordinates with rounding for better precision
452→        // UV origin is bottom-left, texture origin is top-left, so flip V
453→        int x = (int)Math.Round(minU * texture.Width);
454→        int y = (int)Math.Round((1 - maxV) * texture.Height);
455→        int width = (int)Math.Round((maxU - minU) * texture.Width);
456→        int height = (int)Math.Round((maxV - minV) * texture.Height);
457→
458→        return new Rectangle(x, y, width, height);
459→    }
