461→    /// <summary>
462→    /// Processes Blender collection membership and adds appropriate components.
463→    /// Collection names determine entity behavior (e.g., "Collision" adds BoxCollider).
464→    /// Collection custom properties can configure component settings.
465→    /// </summary>
466→    private void ProcessCollections(Entity entity, BlenderObject meshObj, Vector2 size)
467→    {
468→        if (meshObj.Collections == null || meshObj.Collections.Count == 0)
469→            return;
470→
471→        // Compute visual offset from origin so collision boxes align with rendered sprites.
472→        // SpriteBatch.Draw renders at: position - origin * scale
473→        // In destination space: offset = -(size * originOffset)
474→        var originOffsetX = meshObj.OriginOffset?.X ?? 0.5f;
475→        var originOffsetY = meshObj.OriginOffset?.Y ?? 0.5f;
476→        // Compute float edges of visual bounds relative to entity position
477→        float left = -size.X * originOffsetX;
478→        float top = -size.Y * (1 - originOffsetY);  // Y inverted: Blender Y=0 is bottom
479→        // Snap to smallest enclosing integer rectangle (floor expands left/top, ceiling expands right/bottom)
480→        int intLeft = (int)Math.Floor(left);
481→        int intTop = (int)Math.Floor(top);
482→        int intRight = (int)Math.Ceiling(left + size.X);
483→        int intBottom = (int)Math.Ceiling(top + size.Y);
484→
485→        var boundsOffset = new Point(intLeft, intTop);
486→        var boundsSize = new Point(intRight - intLeft, intBottom - intTop);
487→
488→        // Check for Collision collection
489→        if (meshObj.Collections.Contains("Collision"))
490→        {
491→            entity.Set(new EntityInfo("Collision", meshObj.Name));
492→
493→            // Get collision layer from collection properties (default to -1 for all layers)
494→            int layer = -1;
495→            bool passive = false;
496→
497→            if (meshObj.CollectionProperties?.TryGetValue("Collision", out var collisionProps) == true)
498→            {
499→                layer = GetIntProperty(collisionProps, "layer", -1);
500→                passive = GetBoolProperty(collisionProps, "passive", false);
501→            }
502→
503→            var bounds = new Rectangle(boundsOffset, boundsSize);
504→            var activeLayers = new HashSet<int> { layer };
505→            entity.Set(new BoxCollider(bounds, activeLayers, passive));
506→
507→            Logger.Debug($"Added BoxCollider to '{meshObj.Name}' (layer: {layer}, passive: {passive})");
508→        }
509→
510→        // Check for Player collection
511→        if (meshObj.Collections.Contains("Player"))
512→        {
513→            entity.Set(new EntityInfo("Player", meshObj.Name));
514→            entity.Set(new BoxCollider(new Rectangle(boundsOffset, boundsSize)));
515→            entity.Set(new RigidBody());
516→            entity.Set(new Velocity());
517→
518→            // Add camera follow target component
519→            entity.Set(new CameraFollowTarget
520→            {
521→                DampingX = 5.0f,
522→                DampingY = 5.0f,
523→                MaxDistanceX = 150.0f,
524→                MaxDistanceY = 100.0f,
525→                IsActive = true
526→            });
527→            Logger.Debug($"Set Player for '{meshObj.Name}'");
528→        }
529→
530→        // Check for Enemy collection
531→        if (meshObj.Collections.Contains("Enemy"))
532→        {
533→            entity.Set(new EntityInfo("Enemy", meshObj.Name));
534→            Logger.Debug($"Set Enemy for '{meshObj.Name}'");
535→        }
536→
537→        // Check for Trigger collection (collision but no physics push)
538→        if (meshObj.Collections.Contains("Trigger"))
539→        {
540→            if (!entity.Has<BoxCollider>())
541→            {
542→                var bounds = new Rectangle(boundsOffset, boundsSize);
543→                entity.Set(new BoxCollider(bounds, passive: true));
544→            }
545→            entity.Set(new EntityInfo("Trigger", meshObj.Name));
546→            Logger.Debug($"Set as Trigger zone for '{meshObj.Name}'");
547→        }
548→
549→        // Invoke registered game-specific collection handlers
550→        foreach (var collection in meshObj.Collections)
551→        {
552→            if (_collectionHandlers.TryGetValue(collection, out var handler))
553→            {
554→                handler(entity, meshObj);
555→            }
556→        }
557→    }
