559→    /// <summary>
560→    /// Applies a collider child's mesh vertices as a ConvexCollider on the parent entity.
561→    /// Replaces any existing BoxCollider (preserving its layer/passive settings).
562→    /// Sets YSortOffset on the parent's SpriteInfo and adds ColliderTag.
563→    /// </summary>
564→    private void ApplyColliderChild(string parentName, BlenderObject colliderObj)
565→    {
566→        if (!_nameToEntity.TryGetValue(parentName, out var parentEntity))
567→        {
568→            Logger.Warning($"Collider child '{colliderObj.Name}': parent entity '{parentName}' not found.");
569→            return;
570→        }
571→
572→        if (colliderObj.Vertices == null || colliderObj.Vertices.Count < 3)
573→        {
574→            Logger.Warning($"Collider child '{colliderObj.Name}': needs at least 3 vertices, got {colliderObj.Vertices?.Count ?? 0}.");
575→            return;
576→        }
577→
578→        // Build model vertices from exported data
579→        var modelVertices = new Vector2[colliderObj.Vertices.Count];
580→        for (var i = 0; i < colliderObj.Vertices.Count; i++)
581→        {
582→            modelVertices[i] = new Vector2(colliderObj.Vertices[i].X, colliderObj.Vertices[i].Y);
583→        }
584→
585→        // Guard against degenerate shapes (e.g. all vertices collinear or on the same axis)
586→        float minX = float.MaxValue, maxX = float.MinValue;
587→        float minY = float.MaxValue, maxY = float.MinValue;
588→        foreach (var v in modelVertices)
589→        {
590→            if (v.X < minX) minX = v.X;
591→            if (v.X > maxX) maxX = v.X;
592→            if (v.Y < minY) minY = v.Y;
593→            if (v.Y > maxY) maxY = v.Y;
594→        }
595→        if (maxX - minX < 0.01f || maxY - minY < 0.01f)
596→        {
597→            Logger.Warning($"Collider child '{colliderObj.Name}': degenerate shape (width={maxX - minX}, height={maxY - minY}), skipping.");
598→            return;
599→        }
600→
601→        // Determine collision layer and passive flag
602→        HashSet<int> activeLayers = null;
603→        bool passive = false;
604→
605→        // If parent already has a BoxCollider (from ProcessCollections), inherit its settings and remove it
606→        if (parentEntity.Has<BoxCollider>())
607→        {
608→            ref readonly var existingBox = ref parentEntity.Get<BoxCollider>();
609→            activeLayers = existingBox.ActiveLayers;
610→            passive = existingBox.Passive;
611→            parentEntity.Remove<BoxCollider>();
612→            Logger.Debug($"Replaced BoxCollider on '{parentName}' with ConvexCollider from '{colliderObj.Name}'.");
613→        }
614→        else
615→        {
616→            // Check collider child's own collection properties for collision settings
617→            if (colliderObj.CollectionProperties != null)
618→            {
619→                foreach (var (_, props) in colliderObj.CollectionProperties)
620→                {
621→                    var layer = GetIntProperty(props, "layer", -1);
622→                    passive = GetBoolProperty(props, "passive", false);
623→                    activeLayers = new HashSet<int> { layer };
624→                    break;
625→                }
626→            }
627→        }
628→
629→        // Create ConvexCollider with rotation ignored (baked into model vertices)
630→        parentEntity.Set(new ConvexCollider(modelVertices, activeLayers, passive, ignoreTransformRotation: true));
631→
632→        // Set YSortOffset = max Y of model vertices (bottom of collider in Y-down coords)
633→        // maxY was already computed in the degenerate-shape guard above
634→        if (parentEntity.Has<SpriteInfo>())
635→        {
636→            ref var spriteInfo = ref parentEntity.Get<SpriteInfo>();
637→            spriteInfo.YSortOffset = maxY;
638→        }
639→
640→        // Add ColliderTag
641→        parentEntity.Set(new ColliderTag());
642→
643→        Logger.Debug($"Applied ConvexCollider from '{colliderObj.Name}' to '{parentName}' ({modelVertices.Length} vertices, YSortOffset={maxY}).");
644→    }
