646→    /// <summary>
647→    /// Gets an integer property from collection properties dictionary.
648→    /// </summary>
649→    private static int GetIntProperty(Dictionary<string, object> props, string key, int defaultValue)
650→    {
651→        if (props == null || !props.TryGetValue(key, out var value))
652→            return defaultValue;
653→
654→        if (value is JsonElement jsonElement)
655→        {
656→            if (jsonElement.TryGetInt32(out var intVal))
657→                return intVal;
658→            if (jsonElement.TryGetDouble(out var doubleVal))
659→                return (int)doubleVal;
660→        }
661→
662→        if (value is int intValue)
663→            return intValue;
664→        if (value is double doubleValue)
665→            return (int)doubleValue;
666→        if (value is float floatValue)
667→            return (int)floatValue;
668→
669→        return defaultValue;
670→    }
671→
672→    /// <summary>
673→    /// Gets a boolean property from collection properties dictionary.
674→    /// </summary>
675→    private static bool GetBoolProperty(Dictionary<string, object> props, string key, bool defaultValue)
676→    {
677→        if (props == null || !props.TryGetValue(key, out var value))
678→            return defaultValue;
679→
680→        if (value is JsonElement jsonElement)
681→        {
682→            if (jsonElement.ValueKind == JsonValueKind.True)
683→                return true;
684→            if (jsonElement.ValueKind == JsonValueKind.False)
685→                return false;
686→        }
687→
688→        if (value is bool boolValue)
689→            return boolValue;
690→
691→        return defaultValue;
692→    }
