  1→using System;
  2→using System.Collections.Generic;
  3→
  4→namespace MonoDreams.Draw;
  5→
  6→/// <summary>
  7→/// Maps named draw layers to evenly-spaced depth values.
  8→/// Created from any enum via <see cref="FromEnum{TEnum}"/>: the first member maps to 1.0 (front),
  9→/// the last to 0.0 (back), with even spacing in between.
 10→/// Supports type-safe lookup for game code and string-based lookup for core systems (e.g. Blender parser).
 11→/// </summary>
 12→public sealed class DrawLayerMap
 13→{
 14→    private readonly Dictionary<string, float> _layers;
 15→    private readonly float _step;
 16→    private readonly HashSet<float> _ySortedDepths = new();
 17→
 18→    private DrawLayerMap(Dictionary<string, float> layers, float step)
 19→    {
 20→        _layers = layers;
 21→        _step = step;
 22→    }
 23→
 24→    /// <summary>
 25→    /// Creates a DrawLayerMap from an enum type. First member = 1.0 (front), last = 0.0 (back).
 26→    /// </summary>
 27→    public static DrawLayerMap FromEnum<TEnum>() where TEnum : struct, Enum
 28→    {
 29→        var values = Enum.GetValues<TEnum>();
 30→        var length = values.Length;
 31→        var layers = new Dictionary<string, float>(length, StringComparer.OrdinalIgnoreCase);
 32→
 33→        float step = length <= 1 ? 1f : 1f / (length - 1);
 34→
 35→        for (int i = 0; i < length; i++)
 36→        {
 37→            float depth = length == 1 ? 0.5f : (length - 1 - i) / (float)(length - 1);
 38→            layers[values[i].ToString()] = depth;
 39→        }
 40→
 41→        return new DrawLayerMap(layers, step);
 42→    }
 43→
 44→    /// <summary>
 45→    /// Gets the depth for a typed enum layer.
 46→    /// </summary>
 47→    public float GetDepth<TEnum>(TEnum layer) where TEnum : struct, Enum
 48→    {
 49→        return _layers[layer.ToString()];
 50→    }
 51→
 52→    /// <summary>
 53→    /// Gets the depth for a typed enum layer with a sub-layer offset.
 54→    /// </summary>
 55→    public float GetDepth<TEnum>(TEnum layer, float subLayerOffset) where TEnum : struct, Enum
 56→    {
 57→        return _layers[layer.ToString()] + subLayerOffset;
 58→    }
 59→
 60→    /// <summary>
 61→    /// Gets the depth for a layer by name (case-insensitive). For use by core systems.
 62→    /// </summary>
 63→    public float GetDepth(string layerName)
 64→    {
 65→        return _layers[layerName];
 66→    }
 67→
 68→    /// <summary>
 69→    /// Gets the depth for a layer by name with a sub-layer offset.
 70→    /// </summary>
 71→    public float GetDepth(string layerName, float subLayerOffset)
 72→    {
 73→        return _layers[layerName] + subLayerOffset;
 74→    }
 75→
 76→    /// <summary>
 77→    /// Tries to get the depth for a layer by name (case-insensitive).
 78→    /// Returns false if the layer name is not found.
 79→    /// </summary>
 80→    public bool TryGetDepth(string layerName, out float depth)
 81→    {
 82→        return _layers.TryGetValue(layerName, out depth);
 83→    }
 84→
 85→    /// <summary>
 86→    /// Registers an alias that maps to the same depth as an existing enum layer.
 87→    /// All string-based lookups (TryGetDepth, GetDepth) work transparently with aliases.
 88→    /// </summary>
 89→    public DrawLayerMap WithAlias<TEnum>(string alias, TEnum layer) where TEnum : struct, Enum
 90→    {
 91→        var layerName = layer.ToString();
 92→        if (!_layers.TryGetValue(layerName, out var depth))
 93→            throw new ArgumentException($"Layer '{layerName}' not found in map.", nameof(layer));
 94→        _layers[alias] = depth;
 95→        return this;
 96→    }
 97→
 98→    /// <summary>
 99→    /// Marks a layer as Y-sorted. Entities on this layer will have their depth dynamically
100→    /// adjusted based on Y position (lower on screen = rendered in front).
101→    /// </summary>
102→    public DrawLayerMap WithYSort<TEnum>(TEnum layer) where TEnum : struct, Enum
103→    {
104→        _ySortedDepths.Add(_layers[layer.ToString()]);
105→        return this;
106→    }
107→
108→    /// <summary>
109→    /// Returns the Y-sort interpolation range for a depth value, if it belongs to a Y-sorted layer.
110→    /// The range is slightly inset from the full layer range to prevent overlap with adjacent layers.
111→    /// </summary>
112→    public bool TryGetYSortRange(float depth, out float minDepth, out float maxDepth)
113→    {
114→        if (_ySortedDepths.Contains(depth))
115→        {
116→            const float margin = 0.001f;
117→            float halfStep = _step / 2f;
118→            minDepth = depth - halfStep + margin;
119→            maxDepth = depth + halfStep - margin;
120→            return true;
121→        }
122→
123→        minDepth = 0f;
124→        maxDepth = 0f;
125→        return false;
126→    }
127→}
