 1→using System;
 2→using System.Collections.Generic;
 3→using DefaultEcs;
 4→using DefaultEcs.System;
 5→using Microsoft.Xna.Framework;
 6→using MonoDreams.Component;
 7→using MonoDreams.Component.Draw;
 8→using MonoDreams.Draw;
 9→using MonoDreams.State;
10→
11→namespace MonoDreams.System.Draw;
12→
13→/// <summary>
14→/// Dynamically adjusts DrawComponent.LayerDepth for entities on Y-sorted layers,
15→/// so that entities lower on screen render in front of entities higher on screen.
16→/// Runs after SpritePrepSystem (which sets LayerDepth from SpriteInfo) and overwrites
17→/// LayerDepth with the Y-interpolated value for participating entities.
18→///
19→/// A PostUpdate pass re-derives biased children's depth from their parent's final depth,
20→/// ensuring parent-child pairs always sort as a group (no external entity can slip between them).
21→/// </summary>
22→[With(typeof(DrawComponent), typeof(SpriteInfo), typeof(Transform), typeof(Visible))]
23→public class YSortSystem(World world, MonoDreams.Component.Camera camera, DrawLayerMap drawLayerMap)
24→    : AEntitySetSystem<GameState>(world)
25→{
26→    private float _boundsTop;
27→    private float _boundsHeight;
28→    private readonly List<Entity> _biasedEntities = new();
29→
30→    protected override void PreUpdate(GameState state)
31→    {
32→        var bounds = camera.VirtualScreenBounds;
33→        _boundsTop = bounds.Top;
34→        _boundsHeight = bounds.Height;
35→        _biasedEntities.Clear();
36→    }
37→
38→    protected override void Update(GameState state, in Entity entity)
39→    {
40→        ref readonly var spriteInfo = ref entity.Get<SpriteInfo>();
41→
42→        if (!drawLayerMap.TryGetYSortRange(spriteInfo.LayerDepth, out var minDepth, out var maxDepth))
43→            return;
44→
45→        ref readonly var transform = ref entity.Get<Transform>();
46→        ref var drawComponent = ref entity.Get<DrawComponent>();
47→
48→        // Normalize Y within camera bounds: 0 = top, 1 = bottom
49→        float t = _boundsHeight > 0
50→            ? Math.Clamp((transform.WorldPosition.Y + spriteInfo.YSortOffset - _boundsTop) / _boundsHeight, 0f, 1f)
51→            : 0.5f;
52→
53→        // Higher t (lower on screen) → higher depth → rendered in front
54→        drawComponent.LayerDepth = Math.Clamp(
55→            minDepth + t * (maxDepth - minDepth) + spriteInfo.YSortDepthBias,
56→            minDepth,
57→            maxDepth);
58→
59→        if (spriteInfo.YSortDepthBias != 0f)
60→            _biasedEntities.Add(entity);
61→    }
62→
63→    protected override void PostUpdate(GameState state)
64→    {
65→        if (_biasedEntities.Count == 0) return;
66→
67→        var hierarchy = world.Has<EntityHierarchy>() ? world.Get<EntityHierarchy>() : null;
68→        if (hierarchy == null) return;
69→
70→        foreach (var child in _biasedEntities)
71→        {
72→            var parent = hierarchy.GetParent(child);
73→            if (!parent.HasValue || !parent.Value.IsAlive) continue;
74→            if (!parent.Value.Has<DrawComponent>()) continue;
75→
76→            ref readonly var parentDraw = ref parent.Value.Get<DrawComponent>();
77→            ref readonly var childSprite = ref child.Get<SpriteInfo>();
78→            ref var childDraw = ref child.Get<DrawComponent>();
79→
80→            if (!drawLayerMap.TryGetYSortRange(childSprite.LayerDepth, out var minDepth, out var maxDepth))
81→                continue;
82→
83→            childDraw.LayerDepth = Math.Clamp(
84→                parentDraw.LayerDepth + childSprite.YSortDepthBias,
85→                minDepth,
86→                maxDepth);
87→        }
88→    }
89→}
