  1→using System.Collections.Generic;
  2→using DefaultEcs;
  3→using DefaultEcs.System;
  4→using MonoDreams.Component;
  5→using MonoDreams.State;
  6→
  7→namespace MonoDreams.System;
  8→
  9→/// <summary>
 10→/// Maintains the EntityHierarchy resource, syncs Transform.Parent from ChildOf,
 11→/// propagates dirty flags from parent to child transforms, and cascade-disposes
 12→/// orphaned children (whose parent entity is no longer alive).
 13→///
 14→/// Replaces TransformHierarchySystem. Must run AFTER logic systems modify transforms
 15→/// but BEFORE any systems that read world transforms (camera, rendering).
 16→/// </summary>
 17→public class HierarchySystem : ISystem<GameState>
 18→{
 19→    private readonly World _world;
 20→    private readonly EntitySet _childOfSet;
 21→    private readonly EntitySet _transformSet;
 22→    private readonly EntityHierarchy _hierarchy;
 23→    private readonly Dictionary<Transform, List<Entity>> _parentToChildren = new();
 24→
 25→    public bool IsEnabled { get; set; } = true;
 26→
 27→    public HierarchySystem(World world)
 28→    {
 29→        _world = world;
 30→        _childOfSet = world.GetEntities().With<ChildOf>().AsSet();
 31→        _transformSet = world.GetEntities().With<Transform>().AsSet();
 32→
 33→        // Register the hierarchy as a managed resource on the world
 34→        _hierarchy = new EntityHierarchy();
 35→        world.Set(_hierarchy);
 36→    }
 37→
 38→    public void Update(GameState state)
 39→    {
 40→        if (!IsEnabled) return;
 41→
 42→        // Step 1: Detect orphans and cascade-dispose them
 43→        DisposeOrphans();
 44→
 45→        // Step 2: Rebuild the EntityHierarchy lookup from current ChildOf components
 46→        _hierarchy.Rebuild(_childOfSet.GetEntities());
 47→
 48→        // Step 3: Sync Transform.Parent from ChildOf for entities that have both
 49→        SyncTransformParents();
 50→
 51→        // Step 4: Propagate dirty flags from parent to children (same as old TransformHierarchySystem)
 52→        PropagateDirtyFlags();
 53→    }
 54→
 55→    private void DisposeOrphans()
 56→    {
 57→        var entities = _childOfSet.GetEntities();
 58→        // Collect orphans first to avoid modifying the set during iteration
 59→        List<Entity> orphans = null;
 60→
 61→        foreach (var entity in entities)
 62→        {
 63→            if (!entity.IsAlive) continue;
 64→            var parent = entity.Get<ChildOf>().Parent;
 65→            if (!parent.IsAlive)
 66→            {
 67→                orphans ??= new List<Entity>();
 68→                orphans.Add(entity);
 69→            }
 70→        }
 71→
 72→        if (orphans != null)
 73→        {
 74→            foreach (var orphan in orphans)
 75→            {
 76→                if (orphan.IsAlive)
 77→                {
 78→                    Logger.Debug($"Cascade-disposing orphan entity (parent destroyed)");
 79→                    orphan.Dispose();
 80→                }
 81→            }
 82→        }
 83→    }
 84→
 85→    private void SyncTransformParents()
 86→    {
 87→        foreach (var entity in _childOfSet.GetEntities())
 88→        {
 89→            if (!entity.IsAlive || !entity.Has<Transform>()) continue;
 90→
 91→            var parentEntity = entity.Get<ChildOf>().Parent;
 92→            if (!parentEntity.IsAlive || !parentEntity.Has<Transform>()) continue;
 93→
 94→            ref var childTransform = ref entity.Get<Transform>();
 95→            var parentTransform = parentEntity.Get<Transform>();
 96→
 97→            // Only sync if Transform.Parent doesn't already point to the right transform
 98→            if (childTransform.Parent != parentTransform)
 99→            {
100→                childTransform.Parent = parentTransform;
101→            }
102→        }
103→    }
104→
105→    private void PropagateDirtyFlags()
106→    {
107→        // Clear list contents but keep the lists allocated to reduce GC pressure
108→        foreach (var list in _parentToChildren.Values)
109→            list.Clear();
110→
111→        foreach (var entity in _transformSet.GetEntities())
112→        {
113→            ref var transform = ref entity.Get<Transform>();
114→            if (transform.Parent != null)
115→            {
116→                if (!_parentToChildren.TryGetValue(transform.Parent, out var children))
117→                {
118→                    children = new List<Entity>();
119→                    _parentToChildren[transform.Parent] = children;
120→                }
121→                children.Add(entity);
122→            }
123→        }
124→
125→        // Propagate dirty flags
126→        foreach (var entity in _transformSet.GetEntities())
127→        {
128→            ref var transform = ref entity.Get<Transform>();
129→            if (transform.IsDirty && _parentToChildren.ContainsKey(transform))
130→            {
131→                PropagateIsDirtyToChildren(transform);
132→            }
133→        }
134→    }
135→
136→    private void PropagateIsDirtyToChildren(Transform parent)
137→    {
138→        if (!_parentToChildren.TryGetValue(parent, out var children))
139→            return;
140→
141→        foreach (var child in children)
142→        {
143→            if (child.IsAlive && child.Has<Transform>())
144→            {
145→                ref var childTransform = ref child.Get<Transform>();
146→                childTransform.SetDirty();
147→
148→                // Recursively propagate to grandchildren
149→                PropagateIsDirtyToChildren(childTransform);
150→            }
151→        }
152→    }
153→
154→    public void Dispose()
155→    {
156→        _childOfSet.Dispose();
157→        _transformSet.Dispose();
158→    }
159→}
