  1→using System.Text.Json;
  2→using DefaultEcs;
  3→using DefaultEcs.System;
  4→using Microsoft.Xna.Framework;
  5→using Microsoft.Xna.Framework.Content;
  6→using Microsoft.Xna.Framework.Graphics;
  7→using System;
  8→using System.Collections.Generic;
  9→using System.IO;
 10→using System.Linq;
 11→using MonoDreams.Component;
 12→using MonoDreams.Component.Collision;
 13→using MonoDreams.Component.Physics;
 14→using MonoDreams.Component.Draw;
 15→using MonoDreams.Level;
 16→using MonoDreams.Message.Level;
 17→using MonoDreams.Draw;
 18→using MonoDreams.Extension;
 19→using MonoDreams.State;
 20→using Logger = MonoDreams.State.Logger;
 21→
 22→namespace MonoDreams.System.Level;
 23→
 24→/// <summary>
 25→/// Parses Blender-exported JSON level files and creates entities for meshes.
 26→/// Handles LoadLevelRequest messages where the identifier starts with "Blender_".
 27→/// </summary>
 28→public sealed class BlenderLevelParserSystem : ISystem<GameState>
 29→{
 30→    private const float DEFAULT_LAYER_DEPTH = 0.5f;
 31→
 32→    private readonly World _world;
 33→    private readonly ContentManager _content;
 34→    private readonly MonoDreams.Component.Camera _camera;
 35→    private readonly Dictionary<string, Texture2D> _loadedTextures = new();
 36→    private readonly HashSet<Entity> _blenderEntities = new();
 37→    private readonly Dictionary<string, Entity> _nameToEntity = new();
 38→    private readonly Dictionary<string, Action<Entity, BlenderObject>> _collectionHandlers = new();
 39→    private DrawLayerMap _drawLayerMap;
 40→
 41→    public bool IsEnabled { get; set; } = true;
 42→
 43→    public BlenderLevelParserSystem(World world, ContentManager content, MonoDreams.Component.Camera camera)
 44→    {
 45→        _world = world ?? throw new ArgumentNullException(nameof(world));
 46→        _content = content ?? throw new ArgumentNullException(nameof(content));
 47→        _camera = camera ?? throw new ArgumentNullException(nameof(camera));
 48→
 49→        _world.Subscribe<LoadLevelRequest>(OnLoadLevelRequest);
 50→    }
 51→
 52→    /// <summary>
 53→    /// Registers a handler that will be called for entities belonging to the specified Blender collection.
 54→    /// This allows game code to add game-specific components to entities created by the core parser.
 55→    /// </summary>
 56→    public void RegisterCollectionHandler(string collectionName, Action<Entity, BlenderObject> handler)
 57→    {
 58→        _collectionHandlers[collectionName] = handler ?? throw new ArgumentNullException(nameof(handler));
 59→    }
 60→
 61→    /// <summary>
 62→    /// Sets the draw layer map used for depth resolution. When set, mesh depth is resolved by:
 63→    /// 1. Object's "drawLayer" custom property (string matching a layer name)
 64→    /// 2. Blender collection names matched against layer names
 65→    /// 3. Falls back to DEFAULT_LAYER_DEPTH
 66→    /// </summary>
 67→    public void SetDrawLayerMap(DrawLayerMap drawLayerMap)
 68→    {
 69→        _drawLayerMap = drawLayerMap ?? throw new ArgumentNullException(nameof(drawLayerMap));
 70→    }
 71→
 72→    [Subscribe]
 73→    private void OnLoadLevelRequest(in LoadLevelRequest request)
 74→    {
 75→        if (!IsEnabled) return;
 76→
 77→        // Only handle Blender levels
 78→        if (!request.LevelIdentifier.StartsWith("Blender_"))
 79→            return;
 80→
 81→        Logger.Info($"Loading Blender level: {request.LevelIdentifier}");
 82→
 83→        // Clean up previous entities
 84→        CleanupEntities();
 85→
 86→        try
 87→        {
 88→            // Load JSON file from Content directory
 89→            // The Content.RootDirectory gives us the path to the Content folder
 90→            var jsonPath = Path.Combine(AppDomain.CurrentDomain.BaseDirectory, _content.RootDirectory, "blender_level.json");
 91→            Logger.Debug($"Loading Blender level from: {jsonPath}");
 92→            var jsonContent = File.ReadAllText(jsonPath);
 93→
 94→            var options = new JsonSerializerOptions
 95→            {
 96→                PropertyNameCaseInsensitive = true
 97→            };
 98→
 99→            var levelData = JsonSerializer.Deserialize<BlenderLevelData>(jsonContent, options);
100→
101→            if (levelData?.Objects == null)
102→            {
103→                Logger.Warning("No objects found in Blender level data.");
104→                return;
105→            }
106→
107→            Logger.Info($"Loaded {levelData.Objects.Count} objects from Blender level.");
108→
109→            // Pre-scan: identify collider children (names ending with "-collider" that have a parent)
110→            var colliderChildMap = new Dictionary<string, BlenderObject>();   // parent name → collider data
111→            var colliderChildNames = new HashSet<string>();
112→            foreach (var obj in levelData.Objects)
113→            {
114→                if (obj.Name.EndsWith("-collider") && !string.IsNullOrEmpty(obj.Parent))
115→                {
116→                    colliderChildMap[obj.Parent] = obj;
117→                    colliderChildNames.Add(obj.Name);
118→                    Logger.Debug($"Found collider child '{obj.Name}' for parent '{obj.Parent}'.");
119→                }
120→            }
121→
122→            // Pass 1: Create all entities (skip collider children)
123→            foreach (var obj in levelData.Objects)
124→            {
125→                if (colliderChildNames.Contains(obj.Name))
126→                {
127→                    Logger.Debug($"Skipping collider child '{obj.Name}' (will be applied to parent).");
128→                    continue;
129→                }
130→
131→                switch (obj.Type)
132→                {
133→                    case "CAMERA":
134→                        ProcessCamera(obj, levelData.ScaleFactor);
135→                        break;
136→                    case "MESH":
137→                    case "GREASEPENCIL":
138→                        ProcessMesh(obj, levelData);
139→                        break;
140→                    case "EMPTY":
141→                        ProcessEmpty(obj);
142→                        break;
143→                    default:
144→                        Logger.Warning($"Skipping unknown object type: {obj.Type}");
145→                        break;
146→                }
147→            }
148→
149→            // Post-Pass 1: Apply collider children to parent entities
150→            foreach (var (parentName, colliderObj) in colliderChildMap)
151→            {
152→                ApplyColliderChild(parentName, colliderObj);
153→            }
154→
155→            // Pass 2: Set up parent-child relationships
156→            foreach (var obj in levelData.Objects)
157→            {
158→                if (string.IsNullOrEmpty(obj.Parent)) continue;
159→                if (colliderChildNames.Contains(obj.Name)) continue;
160→
161→                if (!_nameToEntity.TryGetValue(obj.Name, out var childEntity))
162→                {
163→                    Logger.Warning($"Parent linkage: child '{obj.Name}' not found in entity map.");
164→                    continue;
165→                }
166→                if (!_nameToEntity.TryGetValue(obj.Parent, out var parentEntity))
167→                {
168→                    Logger.Warning($"Parent linkage: parent '{obj.Parent}' not found for child '{obj.Name}'.");
169→                    continue;
170→                }
171→
172→                childEntity.SetParent(parentEntity);
173→                Logger.Debug($"Set parent of '{obj.Name}' to '{obj.Parent}'.");
174→            }
175→
176→            // Post-Pass 2: Propagate YSortOffset from parents to sprite children
177→            foreach (var obj in levelData.Objects)
178→            {
179→                if (string.IsNullOrEmpty(obj.Parent)) continue;
180→                if (colliderChildNames.Contains(obj.Name)) continue;
181→
182→                if (!_nameToEntity.TryGetValue(obj.Name, out var childEntity)) continue;
183→                if (!_nameToEntity.TryGetValue(obj.Parent, out var parentEntity)) continue;
184→
185→                if (childEntity.Has<SpriteInfo>() && parentEntity.Has<SpriteInfo>())
186→                {
187→                    ref readonly var parentSprite = ref parentEntity.Get<SpriteInfo>();
188→                    if (parentSprite.YSortOffset != 0f)
189→                    {
190→                        ref var childSprite = ref childEntity.Get<SpriteInfo>();
191→                        childSprite.YSortOffset = parentSprite.YSortOffset;
192→                        Logger.Debug($"Propagated YSortOffset={parentSprite.YSortOffset} from '{obj.Parent}' to '{obj.Name}'.");
193→                    }
194→                }
195→            }
196→
197→            Logger.Info($"Created {_blenderEntities.Count} entities from Blender level.");
198→        }
199→        catch (Exception ex)
200→        {
201→            Logger.Error($"Error loading Blender level: {ex.Message}\n{ex.StackTrace}");
202→        }
203→    }
204→
205→    private void ProcessCamera(BlenderObject cameraObj, float scaleFactor)
206→    {
207→        if (cameraObj.Position == null) return;
208→
209→        var cameraPosition = new Vector2(cameraObj.Position.X, cameraObj.Position.Y);
210→        _camera.Position = cameraPosition;
211→
212→        // Read ortho_scale from custom properties and convert to game zoom
213→        // Blender ortho_scale = visible width in Blender units
214→        // With scaleFactor, visible world pixels = ortho_scale * scaleFactor
215→        // Game zoom = virtualWidth / visible_world_pixels
216→        if (cameraObj.CustomProperties?.TryGetValue("zoom", out var zoomValue) == true)
217→        {
218→            if (zoomValue is JsonElement jsonElement && jsonElement.TryGetDouble(out var orthoScale))
219→            {
220→                // Convert Blender ortho_scale to game zoom
221→                var visibleWorldWidth = orthoScale * scaleFactor;
222→                var gameZoom = _camera.VirtualWidth / visibleWorldWidth;
223→                _camera.Zoom = (float)gameZoom;
224→                Logger.Debug($"Set camera zoom to {gameZoom} (from Blender ortho_scale {orthoScale}, scaleFactor {scaleFactor})");
225→            }
226→        }
227→
228→        Logger.Debug($"Set camera position to ({cameraPosition.X}, {cameraPosition.Y})");
229→    }
