694→    private void CleanupEntities()
695→    {
696→        Logger.Info($"Cleaning up {_blenderEntities.Count} Blender entities...");
697→        foreach (var entity in _blenderEntities.ToArray())
698→        {
699→            if (entity.IsAlive)
700→            {
701→                entity.Dispose();
702→            }
703→        }
704→        _blenderEntities.Clear();
705→        _nameToEntity.Clear();
706→    }
