 1→using DefaultEcs;
 2→using MonoDreams.Component;
 3→
 4→namespace MonoDreams.Extension;
 5→
 6→/// <summary>
 7→/// Extension methods for setting up entity parent-child relationships via ChildOf.
 8→/// </summary>
 9→public static class EntityHierarchyExtensions
10→{
11→    /// <summary>
12→    /// Sets a structural parent-child relationship. Also syncs Transform.Parent if both entities have a Transform.
13→    /// </summary>
14→    public static void SetParent(this Entity child, Entity parent)
15→    {
16→        child.Set(new ChildOf(parent));
17→
18→        // Eagerly sync Transform.Parent if both have Transform
19→        if (child.Has<Transform>() && parent.Has<Transform>())
20→        {
21→            var parentTransform = parent.Get<Transform>();
22→            ref var childTransform = ref child.Get<Transform>();
23→            childTransform.Parent = parentTransform;
24→        }
25→    }
26→
27→    /// <summary>
28→    /// Removes the structural parent-child relationship. Snapshots the world position before detaching
29→    /// so the entity stays in place visually.
30→    /// </summary>
31→    public static void RemoveParent(this Entity child)
32→    {
33→        if (!child.Has<ChildOf>()) return;
34→
35→        // Snapshot world position before detaching
36→        if (child.Has<Transform>())
37→        {
38→            ref var childTransform = ref child.Get<Transform>();
39→            if (childTransform.Parent != null)
40→            {
41→                var worldPos = childTransform.WorldPosition;
42→                childTransform.Parent = null;
43→                childTransform.Position = worldPos;
44→            }
45→        }
46→
47→        child.Remove<ChildOf>();
48→    }
49→}
