 1→using System;
 2→using System.Collections.Generic;
 3→using System.Linq;
 4→using DefaultEcs;
 5→using MonoDreams.Component;
 6→
 7→namespace MonoDreams.State;
 8→
 9→/// <summary>
10→/// System-maintained lookup for entity parent-child relationships.
11→/// Rebuilt each frame by HierarchySystem from ChildOf components.
12→/// </summary>
13→public class EntityHierarchy
14→{
15→    private readonly Dictionary<Entity, List<Entity>> _childrenMap = new();
16→    private readonly Dictionary<Entity, Entity> _parentMap = new();
17→
18→    /// <summary>
19→    /// Returns the children of an entity, or an empty list if it has none.
20→    /// </summary>
21→    public IReadOnlyList<Entity> GetChildren(Entity parent)
22→    {
23→        return _childrenMap.TryGetValue(parent, out var children)
24→            ? children
25→            : (IReadOnlyList<Entity>)[];
26→    }
27→
28→    /// <summary>
29→    /// Returns the parent of an entity, or null if it has no parent.
30→    /// </summary>
31→    public Entity? GetParent(Entity child)
32→    {
33→        return _parentMap.TryGetValue(child, out var parent) ? parent : null;
34→    }
35→
36→    /// <summary>
37→    /// Returns true if the entity has any children.
38→    /// </summary>
39→    public bool HasChildren(Entity parent)
40→    {
41→        return _childrenMap.TryGetValue(parent, out var children) && children.Count > 0;
42→    }
43→
44→    /// <summary>
45→    /// Returns entities from the given set that do not have a ChildOf component (i.e., root entities).
46→    /// </summary>
47→    public IEnumerable<Entity> GetRoots(IEnumerable<Entity> entities)
48→    {
49→        return entities.Where(e => e.IsAlive && !_parentMap.ContainsKey(e));
50→    }
51→
52→    /// <summary>
53→    /// Rebuilds the hierarchy from the given entity set. Called by HierarchySystem each frame.
54→    /// </summary>
55→    internal void Rebuild(ReadOnlySpan<Entity> entities)
56→    {
57→        _childrenMap.Clear();
58→        _parentMap.Clear();
59→
60→        foreach (var entity in entities)
61→        {
62→            if (!entity.IsAlive || !entity.Has<ChildOf>()) continue;
63→
64→            var parent = entity.Get<ChildOf>().Parent;
65→            if (!parent.IsAlive) continue;
66→
67→            _parentMap[entity] = parent;
68→
69→            if (!_childrenMap.TryGetValue(parent, out var children))
70→            {
71→                children = new List<Entity>();
72→                _childrenMap[parent] = children;
73→            }
74→
75→            children.Add(entity);
76→        }
77→    }
78→}
