 9→public struct SpriteInfo() : IComponent
10→{
11→    public Texture2D SpriteSheet = null;
12→    public Rectangle Source = default; // Source rectangle in the SpriteSheet
13→    public Vector2 Size = default; // Target rendering size on screen
14→    public Color Color = default;
15→    public RenderTargetID Target = RenderTargetID.Main; // Which RenderTarget this belongs to
16→    public float LayerDepth = 0; // Or use LayerDepth directly
17→    public NinePatchInfo? NinePatchData = null; // Optional
18→    public Vector2 Origin = Vector2.Zero; // Origin point in sprite coordinates (pixels from top-left)
19→    public float YSortDepthBias = 0f; // Applied after Y-sort interpolation for deterministic front/back ordering
20→    public float YSortOffset = 0f; // Y offset added to WorldPosition.Y when computing Y-sort depth (e.g. collider bottom)
21→
