23→    {
24→        SpriteSheet?.Dispose();
25→        GC.SuppressFinalize(this);
26→    }
27→
28→    public Vector2 Offset = Vector2.Zero;
29→
30→    public ISite? Site { get; set; } = null;
31→    public event EventHandler? Disposed = null;
32→}
