     5→/// <summary>
     6→/// Float-precision AABB for collision math. Eliminates integer truncation
     7→/// that occurs when using <see cref="Rectangle"/> with float entity positions.
     8→/// </summary>
     9→public readonly struct CollisionRect(Vector2 position, Vector2 size)
    10→{
    11→    public readonly Vector2 Position = position;
    12→    public readonly Vector2 Size = size;
    13→
    14→    public float Left => Position.X;
    15→    public float Top => Position.Y;
    16→    public float Right => Position.X + Size.X;
    17→    public float Bottom => Position.Y + Size.Y;
    18→    public float Width => Size.X;
    19→    public float Height => Size.Y;
    20→    public Vector2 Center => Position + Size / 2f;
    21→
    22→    public bool Intersects(CollisionRect other)
    23→    {
    24→        return Left < other.Right && Right > other.Left &&
    25→               Top < other.Bottom && Bottom > other.Top;
    26→    }
    27→
    28→    /// <summary>
    29→    /// Creates a CollisionRect from integer bounds offset by a float entity position,
    30→    /// preserving sub-pixel precision that <see cref="RectangleExtensions.AtPosition"/> loses.
    31→    /// </summary>
    32→    public static CollisionRect FromBounds(Rectangle bounds, Vector2 entityPosition)
    33→    {
    34→        return new CollisionRect(
    35→            bounds.Location.ToVector2() + entityPosition,
    36→            bounds.Size.ToVector2());
    37→    }
    38→}
