    94→    private SequentialSystem<GameState> CreateUpdateSystem()
    95→    {
    96→        var inputSystems = new ParallelSystem<GameState>(_parallelRunner,
    97→            new CursorInputSystem(_world),
    98→            new InputMappingSystem(_world)
    99→        );
   100→        
   101→        var levelLoadSystems = new SequentialSystem<GameState>(
   102→            new LevelLoadRequestSystem(_world, _content),
   103→            new BlenderLevelParserSystem(_world, _content, _camera),
   104→            new LDtkTileParserSystem(_world, _content),
   105→            new LDtkEntityParserSystem(_world),
   106→            new EntitySpawnSystem(_world, _content, _renderTargets));
   107→        
   108→        // Collision pipeline must run sequentially (movement → velocity → detect → resolve → commit)
   109→        // Individual systems keep their internal _parallelRunner for entity-level parallelism
   110→        var logicSystems = new SequentialSystem<GameState>(
   111→            new MovementSystem(_world, _parallelRunner),
   112→            new OrbSystem(_world),
   113→            new TransformVelocitySystem(_world, _parallelRunner),
   114→            new TransformCollisionDetectionSystem<CollisionMessage>(_world, _parallelRunner, CollisionMessage.Create),
   115→            new TransformPhysicalCollisionResolutionSystem(_world),
   116→            new TransformCommitSystem(_world, _parallelRunner),
   117→            new TextUpdateSystem(_world), // Logic only
   118→            new DialogueUpdateSystem(_world)
   119→            // ... other game logic systems
   120→        );
   121→        
   122→        // Transform hierarchy system must run AFTER logic systems modify transforms
   123→        // but BEFORE any systems read world transforms (camera, rendering, etc.)
   124→        var transformHierarchySystem = new TransformHierarchySystem(_world);
   125→
   126→        var cameraFollowSystem = new CameraFollowSystem(_world, _camera);
   127→
   128→        // Cursor position must update AFTER camera has moved to avoid 1-frame lag
   129→        var cursorLateUpdateSystem = new CursorPositionSystem(_world, _camera, _viewportManager);
   130→
   131→        var debugSystems = new ParallelSystem<GameState>(_parallelRunner,
   132→            new ColliderDebugSystem(_world, _graphicsDevice)
   133→        );
   134→
   135→        return new SequentialSystem<GameState>(
   136→            // new DebugSystem(_world, _game, _spriteBatch), // If needed
   137→            inputSystems,
   138→            levelLoadSystems,
   139→            logicSystems,
   140→            transformHierarchySystem, // Propagate transform hierarchy dirty flags
   141→            cameraFollowSystem,
   142→            cursorLateUpdateSystem,          // Cursor position updates after camera
   143→            new CursorDrawPrepSystem(_world) // Draw prep after position is finalized
   144→            // debugSystems
   145→        );
   146→    }
