    14→/// <summary>
    15→/// Collision detection system that works with Transform component instead of Position.
    16→/// Uses float-precision CollisionRect to avoid integer truncation artifacts.
    17→/// </summary>
    18→public class TransformCollisionDetectionSystem<TCollisionMessage>(
    19→    World world, IParallelRunner parallelRunner, CreateCollisionMessageDelegate<TCollisionMessage> createCollisionMessage)
    20→    : AEntitySetSystem<GameState>(
    21→        world.GetEntities()
    22→            .With((in BoxCollider c) => !c.Passive && c.Enabled)
    23→            .With<Transform>()
    24→            .AsSet(),
    25→        parallelRunner)
    26→    where TCollisionMessage : ICollisionMessage
    27→{
    28→    private readonly IEnumerable<Entity> _targets = world.GetEntities().With((in BoxCollider c) => c.Enabled).AsEnumerable();
    29→
    30→    protected override void Update(GameState state, in Entity entity)
    31→    {
    32→        var collidable = entity.Get<BoxCollider>();
    33→        var transform = entity.Get<Transform>();
    34→        var dynamicRect = CollisionRect.FromBounds(collidable.Bounds, transform.Position);
    35→        var displacement = transform.Delta;
    36→
    37→        foreach (var target in _targets)
    38→        {
    39→            if (target == entity) continue;
    40→
    41→            var targetCollidable = target.Get<BoxCollider>();
    42→            ICollider colliderA = collidable;
    43→            ICollider colliderB = targetCollidable;
    44→            if (!colliderB.SharesLayerWith(colliderA)) continue;
    45→
    46→            var collides = DynamicRectVsRect(
    47→                dynamicRect,
    48→                displacement,
    49→                CollisionRect.FromBounds(targetCollidable.Bounds, target.Get<Transform>().Position),
    50→                out var contactPoint,
    51→                out var contactNormal,
    52→                out var contactTime);
    53→
    54→            if (!collides) continue;
    55→            if (contactTime < 0f || contactNormal == Vector2.Zero) continue;
    56→
    57→            foreach (var layer in colliderB.SharedLayers(colliderA))
    58→            {
    59→                world.Publish(createCollisionMessage(entity, target, contactPoint, contactNormal, contactTime, layer));
    60→            }
    61→        }
    62→    }
    63→
    64→    private static bool RayVsRect(
    65→        Vector2 rayOrigin, Vector2 rayDirection, CollisionRect target, out Vector2 contactPoint,
    66→        out Vector2 contactNormal, out float closestHit)
    67→    {
    68→        closestHit = float.NaN;
    69→        contactNormal = Vector2.Zero;
    70→        contactPoint = Vector2.Zero;
    71→
    72→        var inverseDirection = Vector2.One / rayDirection;
    73→        var closestDistance = (target.Position - rayOrigin) * inverseDirection;
    74→        var furthestDistance = (target.Position + target.Size - rayOrigin) * inverseDirection;
    75→
    76→        if (float.IsNaN(furthestDistance.Y) || float.IsNaN(furthestDistance.X)) return false;
    77→        if (float.IsNaN(closestDistance.Y) || float.IsNaN(closestDistance.X)) return false;
    78→
    79→        if (closestDistance.X > furthestDistance.X) (closestDistance.X, furthestDistance.X) = (furthestDistance.X, closestDistance.X);
    80→        if (closestDistance.Y > furthestDistance.Y) (closestDistance.Y, furthestDistance.Y) = (furthestDistance.Y, closestDistance.Y);
    81→
    82→        if (closestDistance.X > furthestDistance.Y || closestDistance.Y > furthestDistance.X) return false;
    83→
    84→        closestHit = Math.Max(closestDistance.X, closestDistance.Y);
    85→        var furthestHit = Math.Min(furthestDistance.X, furthestDistance.Y);
    86→
    87→        if (furthestHit < 0) return false;
    88→
    89→        contactPoint = rayOrigin + closestHit * rayDirection;
    90→
    91→        if (closestDistance.X > closestDistance.Y)
    92→            contactNormal = inverseDirection.X < 0 ? new Vector2(1, 0) : new Vector2(-1, 0);
    93→        else if (closestDistance.X < closestDistance.Y)
    94→            contactNormal = inverseDirection.Y < 0 ? new Vector2(0, 1) : new Vector2(0, -1);
    95→
    96→        return true;
    97→    }
    98→
    99→    public static bool DynamicRectVsRect(
   100→        in CollisionRect dynamicRect, in Vector2 displacement, in CollisionRect staticRect,
   101→        out Vector2 contactPoint, out Vector2 contactNormal, out float contactTime)
   102→    {
   103→        if (displacement is { X: 0, Y: 0 })
   104→        {
   105→            contactPoint = Vector2.Zero;
   106→            contactNormal = Vector2.Zero;
   107→            contactTime = 0;
   108→            return dynamicRect.Intersects(staticRect);
   109→        }
   110→
   111→        var expandedTarget = new CollisionRect(
   112→            staticRect.Position - dynamicRect.Size / 2,
   113→            staticRect.Size + dynamicRect.Size);
   114→
   115→        var potentialCollision = RayVsRect(
   116→            dynamicRect.Center, displacement, expandedTarget, out contactPoint,
   117→            out contactNormal, out contactTime);
   118→
   119→        return potentialCollision && contactTime < 1.0f;
   120→    }
   121→}
