     4→namespace MonoDreams.Component.Collision;
     5→
     6→public interface ICollider
     7→{
     8→    HashSet<int> ActiveLayers { get; set; }
     9→    public bool Passive { get; set; }
    10→    public bool Enabled { get; set; }
    11→    
    12→    public bool SharesLayerWith(ICollider other) =>
    13→        (ActiveLayers.Contains(-1) && other.ActiveLayers.Count > 0)
    14→        || (other.ActiveLayers.Contains(-1) && ActiveLayers.Count > 0)
    15→        || ActiveLayers.Overlaps(other.ActiveLayers);
    16→
    17→    public IEnumerable<int> SharedLayers(ICollider other)
    18→    {
    19→        if (ActiveLayers.Contains(-1) && other.ActiveLayers.Count > 0) return other.ActiveLayers;
    20→        if (other.ActiveLayers.Contains(-1) && ActiveLayers.Count > 0) return ActiveLayers;
    21→        return ActiveLayers.Intersect(other.ActiveLayers);
    22→    }
    23→}

