    28→    
    29→    protected override void Update(GameState state, in Entity entity)
    30→    {
    31→        var collidable = entity.Get<TCollidableComponent>();
    32→        var position = entity.Get<TPositionComponent>();
    33→        var dynamicRect = collidable.Bounds.AtPosition(position.Current);
    34→        var displacement = position.Delta;
    35→        foreach (var target in _targets)
    36→        {
    37→            if (target == entity) continue;
    38→
    39→            var targetCollidable = target.Get<TCollidableComponent>();
    40→            if (!targetCollidable.SharesLayerWith(collidable)) continue;
    41→
    42→            var collides = DynamicRectVsRect(
    43→                dynamicRect,
    44→                displacement, 
    45→                targetCollidable.Bounds.AtPosition(target.Get<TPositionComponent>().Current),
    46→                out var contactPoint,
    47→                out var contactNormal,
    48→                out var contactTime);
    49→                
    50→            if (!collides) continue;
    51→
    52→            foreach (var layer in targetCollidable.SharedLayers(collidable))
    53→            {
    54→                world.Publish(createCollisionMessage(entity, target, contactPoint, contactNormal, contactTime, layer));
    55→            }
    56→        }
    57→    }
    58→    
    59→    private static bool RayVsRect(
    60→        Vector2 rayOrigin, Vector2 rayDirection, Rectangle target, out Vector2 contactPoint,
    61→        out Vector2 contactNormal, out float closestHit)
    62→    {
    63→        closestHit = float.NaN;
    64→        contactNormal = Vector2.Zero;
    65→        contactPoint = Vector2.Zero;
    66→
    67→        // Cache division
    68→        var inverseDirection = Vector2.One / rayDirection;
    69→
    70→        // Calculate intersections with rectangle bounding axes
    71→        var closestDistance = (target.Location.ToVector2() - rayOrigin) * inverseDirection;
    72→        var furthestDistance = (target.Location.ToVector2() + target.Size.ToVector2() - rayOrigin) * inverseDirection;
    73→
    74→        if (float.IsNaN(furthestDistance.Y) || float.IsNaN(furthestDistance.X)) return false;
    75→        if (float.IsNaN(closestDistance.Y) || float.IsNaN(closestDistance.X)) return false;
    76→
    77→        // Sort distances
    78→        if (closestDistance.X > furthestDistance.X) Utils.Swap(ref closestDistance.X, ref furthestDistance.X);
    79→        if (closestDistance.Y > furthestDistance.Y) Utils.Swap(ref closestDistance.Y, ref furthestDistance.Y);
    80→
    81→        // Early rejection
    82→        if (closestDistance.X > furthestDistance.Y || closestDistance.Y > furthestDistance.X) return false;
    83→
    84→        // Closest 'time' will be the first contact
    85→        closestHit = Math.Max(closestDistance.X, closestDistance.Y);
    86→
    87→        // Furthest 'time' is contact on opposite side of target
    88→        var furthestHit = Math.Min(furthestDistance.X, furthestDistance.Y);
    89→
    90→        // Reject if ray direction is pointing away from object
    91→        if (furthestHit < 0)
    92→            return false;
    93→
    94→        // Contact point of collision from parametric line equation
    95→        contactPoint = rayOrigin + closestHit * rayDirection;
    96→
    97→        if (closestDistance.X > closestDistance.Y)
    98→            contactNormal = inverseDirection.X < 0 ? new Vector2(1, 0) : new Vector2(-1, 0);
    99→        else if (closestDistance.X < closestDistance.Y)
   100→            contactNormal = inverseDirection.Y < 0 ? new Vector2(0, 1) : new Vector2(0, -1);
   101→
   102→        // Note if t_near == t_far, collision is principally in a diagonal
   103→        // so pointless to resolve. By returning a CN={0,0} even though its
   104→        // considered a hit, the resolver wont change anything.
   105→        return true;
   106→    }
   107→        
   108→    public static bool DynamicRectVsRect(
   109→        in Rectangle dynamicRect, in Vector2 displacement, in Rectangle staticRect,
   110→        out Vector2 contactPoint, out Vector2 contactNormal, out float contactTime)
   111→    {
   112→        // Check if dynamic rectangle is actually moving - if not, we just check for intersection
   113→        if (displacement is { X: 0, Y: 0 })
   114→        {
   115→            contactPoint = Vector2.Zero;
   116→            contactNormal = Vector2.Zero;
   117→            contactTime = 0;
   118→            return dynamicRect.Intersects(staticRect);
   119→        }
   120→
   121→        // Expand target rectangle by source dimensions
   122→        var expandedTarget = new Rectangle(
   123→            (staticRect.Location.ToVector2() - dynamicRect.Size.ToVector2() / 2).ToPoint(),
   124→            staticRect.Size + dynamicRect.Size);
   125→
   126→        var potentialCollision = RayVsRect(
   127→            dynamicRect.Center.ToVector2(), displacement, expandedTarget, out contactPoint,
   128→            out contactNormal, out contactTime);
   129→
   130→        // return potentialCollision && contactTime is >= 0.0f and < 1.0f;
   131→        return potentialCollision && contactTime < 1.0f;
   132→    }
   133→}
