    42→    public LoadLevelExampleGameScreen(Game game, GraphicsDevice graphicsDevice, ContentManager content, Camera camera,
    43→        ViewportManager viewportManager, DefaultParallelRunner parallelRunner, SpriteBatch spriteBatch)
    44→    {
    45→        _game = game;
    46→        _graphicsDevice = graphicsDevice;
    47→        _content = content;
    48→        _camera = camera;
    49→        _viewportManager = viewportManager;
    50→        _parallelRunner = parallelRunner;
    51→        _spriteBatch = spriteBatch;
    52→        _renderTargets = new Dictionary<RenderTargetID, RenderTarget2D>
    53→        {
    54→            { RenderTargetID.Main, new RenderTarget2D(graphicsDevice, _viewportManager.VirtualWidth, _viewportManager.VirtualHeight) },
    55→            { RenderTargetID.UI, new RenderTarget2D(graphicsDevice, _viewportManager.VirtualWidth, _viewportManager.VirtualHeight) },
    56→            { RenderTargetID.HUD, new RenderTarget2D(graphicsDevice, _viewportManager.VirtualWidth, _viewportManager.VirtualHeight) }
    57→        };
    58→        
    59→        camera.Position = new Vector2(0, 0);
    60→        
    61→        _world = new World();
    62→        // _levelLoader = new LevelLoader(_world, graphicsDevice, _content, _spriteBatch, _renderTargets);
    63→        UpdateSystem = CreateUpdateSystem();
    64→        DrawSystem = CreateDrawSystem();
    65→    }
    66→
    67→    public ISystem<GameState> UpdateSystem { get; }
    68→    public ISystem<GameState> DrawSystem { get; }
    69→    public void Load(ScreenController screenController, ContentManager content)
    70→    {
    71→        var cursorTextures = new Dictionary<CursorType, Texture2D>
    72→        {
    73→            [CursorType.Default] = content.Load<Texture2D>("Mouse sprites/Triangle Mouse icon 1"),
    74→            [CursorType.Pointer] = content.Load<Texture2D>("Mouse sprites/Triangle Mouse icon 2"),
    75→            [CursorType.Hand] = content.Load<Texture2D>("Mouse sprites/Catpaw Mouse icon"),
    76→            // Add more cursor types as needed
    77→        };
    78→
    79→        // Create cursor entity
    80→        Objects.Cursor.Create(_world, cursorTextures, RenderTargetID.HUD);
    81→
    82→        // Check if a level was requested from the level selection screen
    83→        var requestedLevel = screenController.Game.Services.GetService(typeof(RequestedLevelComponent)) as RequestedLevelComponent;
    84→        if (requestedLevel != null)
    85→        {
    86→            // Load the requested level
    87→            _world.Publish(new LoadLevelRequest(requestedLevel.LevelIdentifier));
    88→
    89→            // Remove the service so it doesn't interfere with future screen loads
    90→            screenController.Game.Services.RemoveService(typeof(RequestedLevelComponent));
    91→        }
    92→    }
    93→    
    94→    private SequentialSystem<GameState> CreateUpdateSystem()
    95→    {
    96→        var inputSystems = new ParallelSystem<GameState>(_parallelRunner,
    97→            new CursorInputSystem(_world),
    98→            new InputMappingSystem(_world)
    99→        );
   100→        
   101→        var levelLoadSystems = new SequentialSystem<GameState>(
   102→            new LevelLoadRequestSystem(_world, _content),
   103→            new BlenderLevelParserSystem(_world, _content, _camera),
   104→            new LDtkTileParserSystem(_world, _content),
   105→            new LDtkEntityParserSystem(_world),
   106→            new EntitySpawnSystem(_world, _content, _renderTargets));
   107→        
   108→        // Systems that modify component state (can often be parallel)
   109→        var logicSystems = new ParallelSystem<GameState>(_parallelRunner,
   110→            new MovementSystem(_world, _parallelRunner),
   111→            new OrbSystem(_world),
   112→            new TransformVelocitySystem(_world, _parallelRunner),
   113→            new TransformCollisionDetectionSystem<CollisionMessage>(_world, _parallelRunner, CollisionMessage.Create),
   114→            new TransformPhysicalCollisionResolutionSystem(_world),
   115→            new TransformCommitSystem(_world, _parallelRunner),
   116→            new TextUpdateSystem(_world), // Logic only
   117→            new DialogueUpdateSystem(_world)
   118→            // ... other game logic systems
   119→        );
   120→        
   121→        // Transform hierarchy system must run AFTER logic systems modify transforms
   122→        // but BEFORE any systems read world transforms (camera, rendering, etc.)
   123→        var transformHierarchySystem = new TransformHierarchySystem(_world);
   124→
   125→        var cameraFollowSystem = new CameraFollowSystem(_world, _camera);
   126→
   127→        // Cursor position must update AFTER camera has moved to avoid 1-frame lag
   128→        var cursorLateUpdateSystem = new CursorPositionSystem(_world, _camera, _viewportManager);
   129→
   130→        var debugSystems = new ParallelSystem<GameState>(_parallelRunner,
   131→            new ColliderDebugSystem(_world, _graphicsDevice)
   132→        );
   133→
   134→        return new SequentialSystem<GameState>(
   135→            // new DebugSystem(_world, _game, _spriteBatch), // If needed
   136→            inputSystems,
   137→            levelLoadSystems,
   138→            logicSystems,
   139→            transformHierarchySystem, // Propagate transform hierarchy dirty flags
   140→            cameraFollowSystem,
   141→            cursorLateUpdateSystem,          // Cursor position updates after camera
   142→            new CursorDrawPrepSystem(_world) // Draw prep after position is finalized
   143→            // debugSystems
   144→        );
   145→    }
   146→    
   147→    private SequentialSystem<GameState> CreateDrawSystem()
   148→    {
   149→        var pixelPerfectRendering = SettingsManager.Instance.Settings.PixelPerfectRendering;
   150→
   151→        // Systems that prepare DrawComponent based on state (can often be parallel)
   152→        var prepDrawSystems = new SequentialSystem<GameState>( // Or parallel if clearing is handled carefully
   153→            // Optional: A system to clear all DrawComponents first?
   154→            // new ClearDrawComponentSystem(_world),
   155→            new CullingSystem(_world, _camera),
   156→            new DialogueUIRenderPrepSystem(_world),
   157→            new SpritePrepSystem(_world, _graphicsDevice, pixelPerfectRendering),
   158→            new TextPrepSystem(_world, pixelPerfectRendering),
   159→            new MeshPrepSystem(_world)
   160→            // new SpriteDebugSystem(_world)  // Debug visualization for sprite bounds and origins
   161→            // ... other systems preparing DrawElements (UI, particles, etc.)
   162→        );
   163→
   164→        // The single system that handles all rendering (strictly sequential)
   165→        var renderSystem = new MasterRenderSystem(
   166→            _spriteBatch,
   167→            _graphicsDevice,
   168→            _camera,
   169→            _renderTargets, // Pass the dictionary/collection of RTs
   170→            _world
   171→        );
   172→    
   173→        // Final system to draw RenderTargets to backbuffer (if needed)
   174→        var finalDrawToScreenSystem = new FinalDrawSystem(_spriteBatch, _graphicsDevice, _viewportManager, _camera, _renderTargets);
   175→        
   176→        return new SequentialSystem<GameState>(
   177→            prepDrawSystems,
   178→            renderSystem,
   179→            finalDrawToScreenSystem // Draw RTs to screen
   180→            // new DrawDebugSystem(_world, _spriteBatch, _renderer) // If needed
   181→        );
   182→    }
