    79→        if (closestDistance.Y > furthestDistance.Y) (closestDistance.Y, furthestDistance.Y) = (furthestDistance.Y, closestDistance.Y);
    80→
    81→        if (closestDistance.X > furthestDistance.Y || closestDistance.Y > furthestDistance.X) return false;
    82→
    83→        closestHit = Math.Max(closestDistance.X, closestDistance.Y);
    84→        var furthestHit = Math.Min(furthestDistance.X, furthestDistance.Y);
    85→
    86→        if (furthestHit < 0) return false;
    87→
    88→        contactPoint = rayOrigin + closestHit * rayDirection;
    89→
    90→        if (closestDistance.X > closestDistance.Y)
    91→            contactNormal = inverseDirection.X < 0 ? new Vector2(1, 0) : new Vector2(-1, 0);
    92→        else if (closestDistance.X < closestDistance.Y)
    93→            contactNormal = inverseDirection.Y < 0 ? new Vector2(0, 1) : new Vector2(0, -1);
    94→
    95→        return true;
    96→    }
    97→
    98→    public static bool DynamicRectVsRect(
    99→        in CollisionRect dynamicRect, in Vector2 displacement, in CollisionRect staticRect,
   100→        out Vector2 contactPoint, out Vector2 contactNormal, out float contactTime)
   101→    {
   102→        if (displacement is { X: 0, Y: 0 })
   103→        {
   104→            contactPoint = Vector2.Zero;
   105→            contactNormal = Vector2.Zero;
   106→            contactTime = 0;
   107→            return dynamicRect.Intersects(staticRect);
   108→        }
   109→
   110→        var expandedTarget = new CollisionRect(
   111→            staticRect.Position - dynamicRect.Size / 2,
   112→            staticRect.Size + dynamicRect.Size);
   113→
   114→        var potentialCollision = RayVsRect(
   115→            dynamicRect.Center, displacement, expandedTarget, out contactPoint,
   116→            out contactNormal, out contactTime);
   117→
   118→        return potentialCollision && contactTime >= 0f && contactTime < 1.0f;
   119→    }
   120→}
