    18→public sealed class ColliderDebugSystem : ISystem<GameState>
    19→{
    20→    private readonly World _world;
    21→    private readonly Texture2D _pixelTexture;
    22→    private const float DEBUG_LINE_DEPTH = 1f; // Render on top of all elements
    23→    
    24→    public bool IsEnabled { get; set; } = true;
    25→
    26→    public ColliderDebugSystem(World world, GraphicsDevice graphicsDevice)
    27→    {
    28→        _world = world;
    29→        // Create a 2x1 white and transparent pixels texture for drawing lines
    30→        _pixelTexture = new Texture2D(graphicsDevice, 2, 1);
    31→        _pixelTexture.SetData([Color.White, Color.Transparent]);
    32→
    33→
    34→        _world.SubscribeEntityComponentAdded<BoxCollider>(AddDebugLinesToEntity);
    35→    }
    36→
    37→    private void AddDebugLinesToEntity(in Entity entity, in BoxCollider boxCollider)
    38→    {
    39→        ref readonly var transform = ref entity.Get<Transform>();
    40→        Color lineColor;
    41→        if (boxCollider.Enabled)
    42→        {
    43→            lineColor = boxCollider.Passive ? Color.Green : Color.Red;
    44→        }
    45→        else
    46→        {
    47→            lineColor = Color.Gray;
    48→        }
    49→        var r = new Rectangle(0, 0, 1, 1);
    50→        var c = new Rectangle(1, 0, 1, 1);
    51→        var spriteInfo = new SpriteInfo{
    52→            SpriteSheet = _pixelTexture,
    53→            Source = new Rectangle(0, 0, 1, 1),
    54→            Size = boxCollider.Bounds.Dimension(),
    55→            Color = lineColor,
    56→            Target = RenderTargetID.Main,
    57→            LayerDepth = DEBUG_LINE_DEPTH,
    58→            NinePatchData = new NinePatchInfo(0, r, r, r, r, c, r, r, r, r),
    59→            Offset = boxCollider.Bounds.Origin(),
    60→        };
    61→
    62→        var debugEntity = _world.CreateEntity();
    63→        debugEntity.Set(transform);
    64→        debugEntity.Set(spriteInfo);
    65→        debugEntity.Set(new DrawComponent());
    66→
    67→        // var worldBounds = boxCollider.Bounds.AtPosition(position.Current);
    68→
    69→        // Add debug draw elements for the four sides of the rectangle
    70→        // AddDebugLine(drawComponent, worldBounds.Left, worldBounds.Top, worldBounds.Right, worldBounds.Top, lineColor); // Top
    71→        // AddDebugLine(drawComponent, worldBounds.Right - 1, worldBounds.Top, worldBounds.Right - 1, worldBounds.Bottom, lineColor); // Right
    72→        // AddDebugLine(drawComponent, worldBounds.Left, worldBounds.Bottom - 1, worldBounds.Right, worldBounds.Bottom - 1, lineColor); // Bottom
    73→        // AddDebugLine(drawComponent, worldBounds.Left, worldBounds.Top, worldBounds.Left, worldBounds.Bottom, lineColor); // Left
    74→    }
    75→
    76→    private void AddDebugLine(DrawComponent drawComponent, int x1, int y1, int x2, int y2, Color color)
    77→    {
    78→        // Calculate line properties
    79→        var start = new Vector2(x1, y1);
    80→        var end = new Vector2(x2, y2);
    81→        var direction = end - start;
    82→        var length = direction.Length();
    83→        var size = x1 == x2 ? new Vector2(1, length) : new Vector2(length, 1);
    84→
    85→        // Create a DrawElement for the line
    86→        var lineElement = new DrawElement
    87→        {
    88→            Type = DrawElementType.Sprite,
    89→            Target = RenderTargetID.Main,
    90→            Texture = _pixelTexture,
    91→            Position = start,
    92→            Size = size,
    93→            Color = color,
    94→            LayerDepth = DEBUG_LINE_DEPTH,
    95→        };
    96→
    97→        // drawComponent.Drawables.Add(lineElement);
    98→    }
