    10→namespace MonoDreams.Examples.System.Draw;
    11→
    12→[With(typeof(SpriteInfo), typeof(Transform))]
    13→public class CullingSystem(World world, MonoDreams.Component.Camera camera) : AEntitySetSystem<GameState>(world)
    14→{
    15→    public bool IsEnabled { get; set; } = true;
    16→    public bool DebugEnabled { get; set; } = true;
    17→    public int DebugMargin { get; set; } = 50;
    18→
    19→    private Entity? _debugEntity;
    20→    private Rectangle _cullBounds;
    21→
    22→    protected override void PreUpdate(GameState state)
    23→    {
    24→        // Compute the effective culling bounds for this frame
    25→        _cullBounds = camera.VirtualScreenBounds;
    26→
    27→        if (DebugEnabled)
    28→        {
    29→            // DebugMargin is in screen pixels — convert to world units for culling
    30→            var worldMarginX = (int)Math.Min(DebugMargin / camera.Zoom, _cullBounds.Width * 0.5f - 1);
    31→            var worldMarginY = (int)Math.Min(DebugMargin / camera.Zoom, _cullBounds.Height * 0.5f - 1);
    32→            worldMarginX = Math.Max(0, worldMarginX);
    33→            worldMarginY = Math.Max(0, worldMarginY);
    34→
    35→            _cullBounds = new Rectangle(
    36→                _cullBounds.X + worldMarginX,
    37→                _cullBounds.Y + worldMarginY,
    38→                _cullBounds.Width - worldMarginX * 2,
    39→                _cullBounds.Height - worldMarginY * 2);
    40→
    41→            // Create or update the debug outline entity
    42→            if (_debugEntity == null || !_debugEntity.Value.IsAlive)
    43→            {
    44→                _debugEntity = world.CreateEntity();
    45→                _debugEntity.Value.Set(new Transform());
    46→                _debugEntity.Value.Set(new DrawComponent { Target = RenderTargetID.HUD });
    47→                _debugEntity.Value.Set<Visible>();
    48→            }
    49→
    50→            // HUD uses Matrix.Identity — DebugMargin is already in screen pixels
    51→            var debugRect = new Rectangle(
    52→                DebugMargin, DebugMargin,
    53→                camera.VirtualWidth - DebugMargin * 2,
    54→                camera.VirtualHeight - DebugMargin * 2);
    55→
    56→            var generator = new RectangleOutlineMeshGenerator(debugRect, 2f, Color.Lime);
    57→            _debugEntity.Value.Get<DrawComponent>().SetMeshData(generator);
    58→        }
    59→        else if (_debugEntity != null && _debugEntity.Value.IsAlive)
    60→        {
    61→            _debugEntity.Value.Dispose();
    62→            _debugEntity = null;
    63→        }
    64→    }
    65→
    66→    protected override void Update(GameState state, in Entity entity)
    67→    {
    68→        var transform = entity.Get<Transform>();
    69→        var spriteInfo = entity.Get<SpriteInfo>();
    70→
    71→        // Calculate scale from source to destination (matches MasterRenderSystem logic)
    72→        var scaleX = spriteInfo.Source.Width > 0 ? spriteInfo.Size.X / spriteInfo.Source.Width : 1f;
    73→        var scaleY = spriteInfo.Source.Height > 0 ? spriteInfo.Size.Y / spriteInfo.Source.Height : 1f;
    74→
    75→        // Entity bounds must account for origin offset to match how SpriteBatch.Draw renders:
    76→        // visual position = worldPosition + offset - origin * scale
    77→        var entityBounds = new Rectangle(
    78→            (int)(transform.WorldPosition.X + spriteInfo.Offset.X - spriteInfo.Origin.X * scaleX),
    79→            (int)(transform.WorldPosition.Y + spriteInfo.Offset.Y - spriteInfo.Origin.Y * scaleY),
    80→            (int)spriteInfo.Size.X,
    81→            (int)spriteInfo.Size.Y
    82→        );
    83→
    84→        var isVisible = _cullBounds.Intersects(entityBounds);
    85→
    86→        if (isVisible)
    87→        {
    88→            if (!entity.Has<Visible>())
    89→            {
    90→                entity.Set<Visible>();
    91→            }
    92→        }
    93→        else
    94→        {
    95→            if (entity.Has<Visible>())
    96→            {
    97→                entity.Remove<Visible>();
    98→            }
    99→        }
   100→    }
   101→
   102→    public override void Dispose()
   103→    {
   104→        if (_debugEntity != null && _debugEntity.Value.IsAlive)
   105→        {
   106→            _debugEntity.Value.Dispose();
   107→            _debugEntity = null;
   108→        }
   109→        base.Dispose();
   110→    }
   111→}
