    14→/// <summary>
    15→/// Handles button hover detection and click interactions for level selector buttons.
    16→/// </summary>
    17→[With(typeof(LevelSelector), typeof(Transform), typeof(SimpleButton))]
    18→public class ButtonInteractionSystem(World world) : AEntitySetSystem<GameState>(world)
    19→{
    20→    private EntitySet _cursorEntities = world.GetEntities().With<CursorInput>().AsSet();
    21→
    22→    protected override void Update(GameState state, in Entity entity)
    23→    {
    24→        ref var levelSelector = ref entity.Get<LevelSelector>();
    25→        ref readonly var transform = ref entity.Get<Transform>();
    26→        ref readonly var button = ref entity.Get<SimpleButton>();
    27→
    28→        // Get cursor input
    29→        var entities = _cursorEntities.GetEntities();
    30→        if (entities.Length == 0) return;
    31→
    32→        var cursorEntity = entities[0];
    33→        if (!cursorEntity.Has<CursorInput>()) return;
    34→
    35→        ref readonly var cursor = ref cursorEntity.Get<CursorInput>();
    36→
    37→        var buttonBounds = new Rectangle(
    38→            (int)transform.WorldPosition.X,
    39→            (int)transform.WorldPosition.Y,
    40→            (int)button.Size.X,
    41→            (int)button.Size.Y);
    42→
    43→        bool isMouseOver = buttonBounds.Contains(cursor.WorldPosition);
    44→
    45→        // Update hover state
    46→        levelSelector.IsHovered = isMouseOver && levelSelector.IsClickable;
    47→
    48→        // Update text color based on state
    49→        if (button.TextEntity is not null)
    50→        {
    51→            ref var text = ref button.TextEntity.Value.Get<DynamicText>();
    52→
    53→            if (!levelSelector.IsClickable)
    54→            {
    55→                text.Color = levelSelector.DisabledColor;
    56→            }
    57→            else if (levelSelector.IsHovered)
    58→            {
    59→                text.Color = levelSelector.HoveredColor;
    60→            }
    61→            else
    62→            {
    63→                text.Color = levelSelector.DefaultColor;
    64→            }
    65→        }
    66→
    67→        // Handle click - request screen transition to game with level
    68→        if (levelSelector.IsHovered && cursor.LeftButtonReleased)
    69→        {
    70→            World.Publish(new ScreenTransitionRequest(ScreenName.Game, levelSelector.LevelName));
    71→        }
    72→    }
    73→}
