     8→using MonoDreams.Message;
     9→using MonoDreams.State;
    10→using MonoDreams.System.Collision;
    11→
    12→namespace MonoDreams.Examples.System.Collision;
    13→
    14→/// <summary>
    15→/// Collision detection system that works with Transform component instead of Position.
    16→/// Uses float-precision CollisionRect to avoid integer truncation artifacts.
    17→/// </summary>
    18→public class TransformCollisionDetectionSystem<TCollisionMessage>(
    19→    World world, IParallelRunner parallelRunner, CreateCollisionMessageDelegate<TCollisionMessage> createCollisionMessage)
    20→    : AEntitySetSystem<GameState>(
    21→        world.GetEntities()
    22→            .With((in BoxCollider c) => !c.Passive && c.Enabled)
    23→            .With<Transform>()
    24→            .AsSet(),
    25→        parallelRunner)
    26→    where TCollisionMessage : ICollisionMessage
    27→{
    28→    private readonly IEnumerable<Entity> _targets = world.GetEntities().With((in BoxCollider c) => c.Enabled).AsEnumerable();
    29→
    30→    protected override void Update(GameState state, in Entity entity)
    31→    {
    32→        var collidable = entity.Get<BoxCollider>();
    33→        var transform = entity.Get<Transform>();
    34→        var dynamicRect = CollisionRect.FromBounds(collidable.Bounds, transform.Position);
    35→        var displacement = transform.Delta;
    36→
    37→        foreach (var target in _targets)
    38→        {
    39→            if (target == entity) continue;
    40→
    41→            var targetCollidable = target.Get<BoxCollider>();
    42→            ICollider colliderA = collidable;
    43→            ICollider colliderB = targetCollidable;
    44→            if (!colliderB.SharesLayerWith(colliderA)) continue;
    45→
    46→            var collides = DynamicRectVsRect(
    47→                dynamicRect,
    48→                displacement,
    49→                CollisionRect.FromBounds(targetCollidable.Bounds, target.Get<Transform>().Position),
    50→                out var contactPoint,
    51→                out var contactNormal,
    52→                out var contactTime);
    53→
    54→            if (!collides) continue;
    55→
    56→            foreach (var layer in colliderB.SharedLayers(colliderA))
    57→            {
    58→                world.Publish(createCollisionMessage(entity, target, contactPoint, contactNormal, contactTime, layer));
    59→            }
    60→        }
    61→    }
    62→
    63→    private static bool RayVsRect(
    64→        Vector2 rayOrigin, Vector2 rayDirection, CollisionRect target, out Vector2 contactPoint,
    65→        out Vector2 contactNormal, out float closestHit)
    66→    {
    67→        closestHit = float.NaN;
    68→        contactNormal = Vector2.Zero;
    69→        contactPoint = Vector2.Zero;
    70→
    71→        var inverseDirection = Vector2.One / rayDirection;
    72→        var closestDistance = (target.Position - rayOrigin) * inverseDirection;
    73→        var furthestDistance = (target.Position + target.Size - rayOrigin) * inverseDirection;
    74→
    75→        if (float.IsNaN(furthestDistance.Y) || float.IsNaN(furthestDistance.X)) return false;
    76→        if (float.IsNaN(closestDistance.Y) || float.IsNaN(closestDistance.X)) return false;
    77→
    78→        if (closestDistance.X > furthestDistance.X) (closestDistance.X, furthestDistance.X) = (furthestDistance.X, closestDistance.X);
    79→        if (closestDistance.Y > furthestDistance.Y) (closestDistance.Y, furthestDistance.Y) = (furthestDistance.Y, closestDistance.Y);
    80→
    81→        if (closestDistance.X > furthestDistance.Y || closestDistance.Y > furthestDistance.X) return false;
    82→
    83→        closestHit = Math.Max(closestDistance.X, closestDistance.Y);
    84→        var furthestHit = Math.Min(furthestDistance.X, furthestDistance.Y);
    85→
    86→        if (furthestHit < 0) return false;
    87→
    88→        contactPoint = rayOrigin + closestHit * rayDirection;
    89→
    90→        if (closestDistance.X > closestDistance.Y)
    91→            contactNormal = inverseDirection.X < 0 ? new Vector2(1, 0) : new Vector2(-1, 0);
    92→        else if (closestDistance.X < closestDistance.Y)
    93→            contactNormal = inverseDirection.Y < 0 ? new Vector2(0, 1) : new Vector2(0, -1);
    94→
    95→        return true;
    96→    }
    97→
    98→    public static bool DynamicRectVsRect(
    99→        in CollisionRect dynamicRect, in Vector2 displacement, in CollisionRect staticRect,
   100→        out Vector2 contactPoint, out Vector2 contactNormal, out float contactTime)
   101→    {
   102→        if (displacement is { X: 0, Y: 0 })
   103→        {
   104→            contactPoint = Vector2.Zero;
   105→            contactNormal = Vector2.Zero;
   106→            contactTime = 0;
   107→            return dynamicRect.Intersects(staticRect);
   108→        }
   109→
   110→        var expandedTarget = new CollisionRect(
   111→            staticRect.Position - dynamicRect.Size / 2,
   112→            staticRect.Size + dynamicRect.Size);
   113→
   114→        var potentialCollision = RayVsRect(
   115→            dynamicRect.Center, displacement, expandedTarget, out contactPoint,
   116→            out contactNormal, out contactTime);
   117→
   118→        return potentialCollision && contactTime >= 0f && contactTime < 1.0f;
   119→    }
   120→}
   121→

