    16→/// Uses float-precision CollisionRect to avoid integer truncation artifacts.
    17→/// </summary>
    18→public class TransformCollisionResolutionSystem<TCollisionMessage> : ISystem<GameState>
    19→    where TCollisionMessage : ICollisionMessage
    20→{
    21→    private readonly World _world;
    22→    protected readonly List<TCollisionMessage> Collisions;
    23→
    24→    public TransformCollisionResolutionSystem(World world)
    25→    {
    26→        _world = world;
    27→        world.Subscribe(this);
    28→        Collisions = [];
    29→    }
    30→
    31→    [Subscribe]
    32→    protected virtual void On(in TCollisionMessage message) => Collisions.Add(message);
    33→
    34→    public bool IsEnabled { get; set; } = true;
    35→
    36→    public void Dispose()
    37→    {
    38→        Collisions.Clear();
    39→        GC.SuppressFinalize(this);
    40→    }
    41→
    42→    public void Update(GameState state)
    43→    {
    44→        Collisions.Sort((l, r) => l.ContactTime.CompareTo(r.ContactTime));
    45→        foreach (var collision in Collisions)
    46→        {
    47→            ResolveCollision(collision);
    48→        }
    49→        Collisions.Clear();
    50→    }
    51→
    52→    private void ResolveCollision(TCollisionMessage collision)
    53→    {
    54→        var entity = collision.BaseEntity;
    55→        ref var transform = ref entity.Get<Transform>();
    56→        var collidable = entity.Get<BoxCollider>();
    57→        var dynamicRect = CollisionRect.FromBounds(collidable.Bounds, transform.Position);
    58→
    59→        var collidingEntity = collision.CollidingEntity;
    60→        var targetTransform = collidingEntity.Get<Transform>();
    61→        var targetRect = CollisionRect.FromBounds(collidingEntity.Get<BoxCollider>().Bounds, targetTransform.Position);
    62→
    63→        if (!TransformCollisionDetectionSystem<TCollisionMessage>.DynamicRectVsRect(dynamicRect, transform.Delta, targetRect,
    64→                out var contactPoint, out var contactNormal, out var contactTime)) return;
    65→
    66→        if (contactNormal.X != 0)
    67→        {
    68→            transform.SetPositionX(contactPoint.X - dynamicRect.Width / 2f - collidable.Bounds.Location.X);
    69→
    70→            if (entity.Has<Velocity>())
    71→            {
    72→                ref var velocity = ref entity.Get<Velocity>();
    73→                velocity.Current.X = 0;
    74→            }
    75→
    76→            if ((int)Math.Abs(contactPoint.X + dynamicRect.Width / 2f - targetRect.Left) == 0)
    77→            {
    78→                _world.Publish(new RigidBodyTouchMessage(collidingEntity, RelativeReferential.Left));
    79→            }
    80→            else if ((int)Math.Abs(contactPoint.X - dynamicRect.Width / 2f - targetRect.Right) == 0)
    81→            {
    82→                _world.Publish(new RigidBodyTouchMessage(collidingEntity, RelativeReferential.Right));
    83→            }
    84→        }
    85→
    86→        if (contactNormal.Y != 0)
    87→        {
    88→            transform.SetPositionY(contactPoint.Y - dynamicRect.Height / 2f - collidable.Bounds.Location.Y);
    89→
    90→            if (entity.Has<Velocity>())
    91→            {
    92→                ref var velocity = ref entity.Get<Velocity>();
    93→                velocity.Current.Y = 0;
    94→            }
    95→
    96→            if ((int)Math.Abs(contactPoint.Y + dynamicRect.Height / 2f - targetRect.Top) == 0)
    97→            {
    98→                _world.Publish(new RigidBodyTouchMessage(collidingEntity, RelativeReferential.Top));
    99→            }
   100→            else if ((int)Math.Abs(contactPoint.Y - dynamicRect.Height / 2f - targetRect.Bottom) == 0)
   101→            {
   102→                _world.Publish(new RigidBodyTouchMessage(collidingEntity, RelativeReferential.Bottom));
